import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './DonorDashboard.css';

const DonorDashboard = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [donationHistory, setDonationHistory] = useState([]);
  const [donorProfile, setDonorProfile] = useState({});
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('campaigns');
  const [donationAmount, setDonationAmount] = useState('');
  const [selectedCampaign, setSelectedCampaign] = useState(null);
  const [showDonationModal, setShowDonationModal] = useState(false);
  const [smsNotifications, setSmsNotifications] = useState([]);

  const API_BASE_URL = 'http://localhost:8080/api';
  const token = localStorage.getItem('token');

  const axiosConfig = {
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    }
  };

  const fetchCampaigns = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/donor/campaigns`, axiosConfig);
      if (response.data.success) {
        setCampaigns(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching campaigns:', error);
    }
  }, [API_BASE_URL, axiosConfig]);

  const fetchDonationHistory = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/donor/donations`, axiosConfig);
      if (response.data.success) {
        setDonationHistory(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching donation history:', error);
    }
  }, [API_BASE_URL, axiosConfig]);

  const fetchDonorProfile = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/donor/profile`, axiosConfig);
      if (response.data.success) {
        setDonorProfile(response.data.data || {});
      }
    } catch (error) {
      console.error('Error fetching donor profile:', error);
    }
  }, [API_BASE_URL, axiosConfig]);

  const fetchDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      await Promise.all([
        fetchCampaigns(),
        fetchDonationHistory(),
        fetchDonorProfile()
      ]);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  }, [fetchCampaigns, fetchDonationHistory, fetchDonorProfile]);

  useEffect(() => {
    fetchDashboardData();
  }, [fetchDashboardData]);

  // Rest of your component code remains the same...
  const handleDonation = async () => {
    if (!donationAmount || !selectedCampaign) {
      alert('Please enter donation amount and select a campaign');
      return;
    }

    if (parseFloat(donationAmount) < 10) {
      alert('Minimum donation amount is $10');
      return;
    }

    try {
      const donationData = {
        campaignId: selectedCampaign.id,
        amount: parseFloat(donationAmount),
        ngoId: selectedCampaign.ngoId
      };

      const response = await axios.post(`${API_BASE_URL}/donor/donate`, donationData, axiosConfig);
      
      if (response.data.success) {
        const smsMessage = `Thank you for your donation of $${donationAmount} to ${selectedCampaign.title}. Transaction ID: ${response.data.data.id}. Your contribution makes a difference!`;
        setSmsNotifications(prev => [...prev, {
          id: Date.now(),
          message: smsMessage,
          timestamp: new Date(),
          campaignTitle: selectedCampaign.title,
          amount: donationAmount
        }]);

        alert('Donation successful! SMS notification sent.');
        setShowDonationModal(false);
        setDonationAmount('');
        setSelectedCampaign(null);
        
        fetchDonationHistory();
        fetchCampaigns();
      } else {
        alert('Donation failed: ' + response.data.message);
      }
    } catch (error) {
      console.error('Error processing donation:', error);
      alert('Error processing donation. Please try again.');
    }
  };

  const openDonationModal = (campaign) => {
    setSelectedCampaign(campaign);
    setShowDonationModal(true);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const calculateProgress = (raised, target) => {
    return Math.min((raised / target) * 100, 100);
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="donor-dashboard">
      <div className="dashboard-header">
        <h1>Welcome back, {donorProfile.firstName}!</h1>
        <div className="dashboard-stats">
          <div className="stat-card">
            <h3>Total Donations</h3>
            <p className="stat-value">{donationHistory.length}</p>
          </div>
          <div className="stat-card">
            <h3>Total Contributed</h3>
            <p className="stat-value">
              {formatCurrency(donationHistory.reduce((sum, donation) => sum + donation.amount, 0))}
            </p>
          </div>
          <div className="stat-card">
            <h3>Active Campaigns</h3>
            <p className="stat-value">{campaigns.length}</p>
          </div>
        </div>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={`tab-button ${activeTab === 'campaigns' ? 'active' : ''}`}
          onClick={() => setActiveTab('campaigns')}
        >
          Available Campaigns
        </button>
        <button 
          className={`tab-button ${activeTab === 'history' ? 'active' : ''}`}
          onClick={() => setActiveTab('history')}
        >
          Donation History
        </button>
        <button 
          className={`tab-button ${activeTab === 'notifications' ? 'active' : ''}`}
          onClick={() => setActiveTab('notifications')}
        >
          SMS Notifications ({smsNotifications.length})
        </button>
      </div>

      <div className="dashboard-content">
        {activeTab === 'campaigns' && (
          <div className="campaigns-section">
            <h2>Ongoing Campaigns</h2>
            {campaigns.length === 0 ? (
              <div className="empty-state">
                <p>No active campaigns available at the moment.</p>
              </div>
            ) : (
              <div className="campaigns-grid">
                {campaigns.map(campaign => (
                  <div key={campaign.id} className="campaign-card">
                    <div className="campaign-header">
                      <h3>{campaign.title}</h3>
                      <span className="campaign-status">Active</span>
                    </div>
                    <p className="campaign-description">{campaign.description}</p>
                    <div className="campaign-progress">
                      <div className="progress-bar">
                        <div 
                          className="progress-fill"
                          style={{ width: `${calculateProgress(campaign.raisedAmount, campaign.targetAmount)}%` }}
                        ></div>
                      </div>
                      <div className="progress-text">
                        <span>{formatCurrency(campaign.raisedAmount)} raised</span>
                        <span>Goal: {formatCurrency(campaign.targetAmount)}</span>
                      </div>
                    </div>
                    <div className="campaign-meta">
                      <p><strong>NGO:</strong> {campaign.ngoName}</p>
                      <p><strong>Expires:</strong> {formatDate(campaign.expirationTime)}</p>
                    </div>
                    <button 
                      className="donate-button"
                      onClick={() => openDonationModal(campaign)}
                    >
                      Donate Now
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'history' && (
          <div className="history-section">
            <h2>Your Donation History</h2>
            {donationHistory.length === 0 ? (
              <div className="empty-state">
                <p>You haven't made any donations yet.</p>
                <button onClick={() => setActiveTab('campaigns')}>
                  Browse Campaigns
                </button>
              </div>
            ) : (
              <div className="history-table">
                <table>
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Campaign</th>
                      <th>NGO</th>
                      <th>Amount</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {donationHistory.map(donation => (
                      <tr key={donation.id}>
                        <td>{formatDate(donation.donationDate)}</td>
                        <td>{donation.campaignTitle}</td>
                        <td>{donation.ngoName}</td>
                        <td>{formatCurrency(donation.amount)}</td>
                        <td>
                          <span className={`status ${donation.status.toLowerCase()}`}>
                            {donation.status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        )}

        {activeTab === 'notifications' && (
          <div className="notifications-section">
            <h2>SMS Notifications</h2>
            {smsNotifications.length === 0 ? (
              <div className="empty-state">
                <p>No SMS notifications yet.</p>
                <p>You'll receive notifications when you make donations.</p>
              </div>
            ) : (
              <div className="notifications-list">
                {smsNotifications.map(notification => (
                  <div key={notification.id} className="notification-card">
                    <div className="notification-header">
                      <span className="notification-title">
                        Donation Confirmation - {notification.campaignTitle}
                      </span>
                      <span className="notification-time">
                        {formatDate(notification.timestamp)}
                      </span>
                    </div>
                    <p className="notification-message">{notification.message}</p>
                    <div className="notification-amount">
                      Amount: {formatCurrency(notification.amount)}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>

      {showDonationModal && (
        <div className="modal-overlay">
          <div className="donation-modal">
            <div className="modal-header">
              <h3>Make a Donation</h3>
              <button 
                className="close-button"
                onClick={() => setShowDonationModal(false)}
              >
                Ã—
              </button>
            </div>
            <div className="modal-content">
              <div className="campaign-info">
                <h4>{selectedCampaign?.title}</h4>
                <p>{selectedCampaign?.description}</p>
                <p><strong>Target:</strong> {formatCurrency(selectedCampaign?.targetAmount)}</p>
                <p><strong>Raised:</strong> {formatCurrency(selectedCampaign?.raisedAmount)}</p>
              </div>
              <div className="donation-form">
                <label htmlFor="amount">Donation Amount (minimum $10)</label>
                <input
                  type="number"
                  id="amount"
                  min="10"
                  step="0.01"
                  value={donationAmount}
                  onChange={(e) => setDonationAmount(e.target.value)}
                  placeholder="Enter amount"
                />
                <div className="quick-amounts">
                  <button onClick={() => setDonationAmount('25')}>$25</button>
                  <button onClick={() => setDonationAmount('50')}>$50</button>
                  <button onClick={() => setDonationAmount('100')}>$100</button>
                  <button onClick={() => setDonationAmount('250')}>$250</button>
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="cancel-button"
                onClick={() => setShowDonationModal(false)}
              >
                Cancel
              </button>
              <button 
                className="confirm-donate-button"
                onClick={handleDonation}
                disabled={!donationAmount || parseFloat(donationAmount) < 10}
              >
                Donate {donationAmount ? formatCurrency(donationAmount) : ''}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DonorDashboard;
