import React, { useEffect, useState } from "react";

// Some made-up images for demo
const campaignImages = [
  "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1500534314209-a25ddb2bd429?auto=format&fit=crop&w=400&q=80",
];

function getAuthHeaders() {
  return {
    Authorization: `Bearer ${localStorage.getItem("token")}`,
    "Content-Type": "application/json",
  };
}

const DonorDashboard = () => {
  const [profile, setProfile] = useState(null);
  const [donorId, setDonorId] = useState(null);
  const [campaigns, setCampaigns] = useState([]);
  const [donations, setDonations] = useState([]);
  const [donateAmount, setDonateAmount] = useState({});
  const [donateLoading, setDonateLoading] = useState({});
  const [donateStatus, setDonateStatus] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch profile and campaigns on mount
  useEffect(() => {
    fetchProfile();
    fetchCampaigns();
  }, []);

  // Fetch profile and donation history
  const fetchProfile = async () => {
    try {
      const res = await fetch("/api/donor/profile", { headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Failed to fetch profile");
      const data = await res.json();
      setProfile(data.data);
      setDonorId(data.data.donorId);
      fetchDonations(data.data.donorId);
    } catch (err) {
      console.error(err);
      setError("Failed to load profile.");
    }
  };

  // Fetch donation history
  const fetchDonations = async (donorId) => {
    try {
      const res = await fetch(`/api/donor/${donorId}`, { headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Failed to fetch donations");
      const data = await res.json();
      setDonations(data);
    } catch (err) {
      console.error(err);
      setError("Failed to load donation history.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch available campaigns
  const fetchCampaigns = async () => {
    try {
      const res = await fetch("/api/donor/allApproved", { headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Failed to fetch campaigns");
      let data = await res.json();
      // Add made-up names and images for demo
      data = data.map((c, idx) => ({
        ...c,
        name: [
          "Clean Water for All",
          "Feed the Children",
          "Plant a Million Trees",
          "Education for Every Child",
        ][idx % 4],
        image: campaignImages[idx % campaignImages.length],
        goal: c.amount || 10000,
        raised: Math.floor((c.amount || 10000) * (0.2 + 0.6 * Math.random())),
      }));
      setCampaigns(data);
    } catch (err) {
      console.error(err);
      setError("Failed to load campaigns.");
    }
  };

  // Handle donation input
  const handleAmountChange = (campaignId, value) => {
    setDonateAmount({ ...donateAmount, [campaignId]: value });
  };

  // Handle donate button
  const handleDonate = async (campaign) => {
    const amount = parseFloat(donateAmount[campaign.campaignId]);
    if (!amount || amount < 50) {
      setDonateStatus({ ...donateStatus, [campaign.campaignId]: "Amount must be at least 50" });
      return;
    }
    setDonateLoading({ ...donateLoading, [campaign.campaignId]: true });
    setDonateStatus({ ...donateStatus, [campaign.campaignId]: "" });

    // Prepare payload as expected by backend
    const payload = {
      donorId,
      ngoID: campaign.ngoEmail, // assuming ngoEmail is used as ngoID in your backend
      amount,
    };

    try {
      const res = await fetch("/api/donor/donate", {
        method: "POST",
        headers: getAuthHeaders(),
        body: JSON.stringify(payload),
      });

      const data = await res.json();
      if (res.ok && data.success) {
        setDonateStatus({ ...donateStatus, [campaign.campaignId]: "Donation successful!" });
        fetchDonations(donorId);
      } else {
        throw new Error(data.message || "Donation failed");
      }
    } catch (err) {
      console.error(err);
      setDonateStatus({ ...donateStatus, [campaign.campaignId]: err.message });
    } finally {
      setDonateLoading({ ...donateLoading, [campaign.campaignId]: false });
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: 1100, margin: "auto", padding: 24 }}>
      <h2>Donor Dashboard</h2>

      {/* Error Message */}
      {error && <div style={{ color: "red", marginBottom: 16 }}>{error}</div>}

      {/* Profile */}
      <section style={{ marginBottom: 32 }}>
        <h3>Profile</h3>
        {profile ? (
          <div>
            <p>
              <b>Name:</b> {profile.firstName} {profile.lastName}
            </p>
            <p>
              <b>Email:</b> {profile.email}
            </p>
            <p>
              <b>Phone:</b> {profile.phoneNumber}
            </p>
            <p>
              <b>Address:</b> {profile.address}
            </p>
            <p>
              <b>Occupation:</b> {profile.occupation}
            </p>
          </div>
        ) : (
          <p>Profile not found.</p>
        )}
      </section>

      {/* Campaigns */}
      <section style={{ marginBottom: 32 }}>
        <h3>Available Campaigns</h3>
        <div style={{ display: "flex", flexWrap: "wrap", gap: 24 }}>
          {campaigns.length === 0 ? (
            <p>No campaigns available.</p>
          ) : (
            campaigns.map((c, idx) => {
              const percent = Math.min(100, Math.round((c.raised / c.goal) * 100));
              return (
                <div
                  key={c.campaignId || idx}
                  style={{
                    width: 300,
                    border: "1px solid #ccc",
                    borderRadius: 8,
                    padding: 16,
                    background: "#fafafa",
                  }}
                >
                  <img
                    src={c.image}
                    alt={c.name}
                    style={{ width: "100%", height: 140, objectFit: "cover", borderRadius: 6 }}
                  />
                  <h4 style={{ margin: "12px 0 4px" }}>{c.name}</h4>
                  <div style={{ fontSize: 14, color: "#555" }}>{c.description}</div>
                  <div style={{ margin: "10px 0" }}>
                    <div style={{ fontSize: 13, color: "#888" }}>
                      Goal: ${c.goal} &nbsp; Raised: ${c.raised}
                    </div>
                    <div
                      style={{
                        background: "#eee",
                        borderRadius: 6,
                        height: 16,
                        marginTop: 4,
                        marginBottom: 4,
                        overflow: "hidden",
                      }}
                    >
                      <div
                        style={{
                          width: `${percent}%`,
                          background: percent < 100 ? "#4caf50" : "#2196f3",
                          height: "100%",
                          transition: "width 0.5s",
                        }}
                      />
                    </div>
                    <div style={{ fontSize: 12, color: "#666" }}>{percent}% funded</div>
                  </div>
                  <div style={{ marginTop: 10 }}>
                    <input
                      type="number"
                      min={50}
                      placeholder="Amount"
                      value={donateAmount[c.campaignId] || ""}
                      onChange={e => handleAmountChange(c.campaignId, e.target.value)}
                      style={{ width: 100, marginRight: 8 }}
                    />
                    <button
                      disabled={donateLoading[c.campaignId]}
                      onClick={() => handleDonate(c)}
                    >
                      {donateLoading[c.campaignId] ? "Donating..." : "Donate"}
                    </button>
                  </div>
                  {donateStatus[c.campaignId] && (
                    <div style={{ color: donateStatus[c.campaignId].includes("success") ? "green" : "red", fontSize: 13, marginTop: 6 }}>
                      {donateStatus[c.campaignId]}
                    </div>
                  )}
                </div>
              );
            })
          )}
        </div>
      </section>

      {/* Donation History */}
      <section>
        <h3>Donation History</h3>
        {donations.length === 0 ? (
          <p>No donations yet.</p>
        ) : (
          <table border="1" cellPadding="6" style={{ width: "100%", background: "#fff" }}>
            <thead>
              <tr>
                <th>NGO ID</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {donations.map((d, idx) => (
                <tr key={d._id || idx}>
                  <td>{d.ngoId}</td>
                  <td>${d.amount}</td>
                  <td>{d.status}</td>
                  <td>
                    {d.timestamp
                      ? new Date(d.timestamp).toLocaleString()
                      : ""}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default DonorDashboard;
