import React, { useEffect, useState } from "react";
import axios from "axios";

const API_BASE = "/api/ngo"; // Adjust if your API base is different

const NGODashboard = () => {
  const [profile, setProfile] = useState(null);
  const [editProfile, setEditProfile] = useState(false);
  const [profileForm, setProfileForm] = useState({});
  const [campaigns, setCampaigns] = useState([]);
  const [opportunities, setOpportunities] = useState([]);
  const [volunteers, setVolunteers] = useState([]);
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);

  // Assume token is stored in localStorage
  const axiosAuth = axios.create({
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
  });

  useEffect(() => {
    fetchProfile();
    fetchCampaigns();
    fetchOpportunities();
    fetchVolunteers();
    fetchPayments();
  }, []);

  const fetchProfile = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/profile`);
      setProfile(res.data.data);
      setProfileForm(res.data.data);
    } catch (err) {
      alert("Failed to load profile");
    }
    setLoading(false);
  };

  const fetchCampaigns = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/campaigns`);
      setCampaigns(res.data || []);
    } catch (err) {
      setCampaigns([]);
    }
  };

  const fetchOpportunities = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/opportunities`);
      setOpportunities(res.data || []);
    } catch (err) {
      setOpportunities([]);
    }
  };

  const fetchVolunteers = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/volunteers`);
      setVolunteers(res.data.data || []);
    } catch (err) {
      setVolunteers([]);
    }
  };

  const fetchPayments = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/payments`);
      setPayments(res.data || []);
    } catch (err) {
      setPayments([]);
    }
  };

  const handleProfileChange = (e) => {
    setProfileForm({ ...profileForm, [e.target.name]: e.target.value });
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await axiosAuth.put(`${API_BASE}/profile`, profileForm);
      setProfile(res.data.data);
      setEditProfile(false);
      alert("Profile updated!");
    } catch (err) {
      alert("Failed to update profile");
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: 1000, margin: "auto", padding: 24 }}>
      <h2>NGO Dashboard</h2>

      {/* Profile Section */}
      <section style={{ marginBottom: 32 }}>
        <h3>Profile</h3>
        {profile && !editProfile && (
          <div>
            <p>
              <b>Organization:</b> {profile.organizationName}
            </p>
            <p>
              <b>Email:</b> {profile.email}
            </p>
            <p>
              <b>Registration Number:</b> {profile.registrationNumber}
            </p>
            <p>
              <b>Contact Person:</b> {profile.contactPerson}
            </p>
            <p>
              <b>Phone:</b> {profile.phoneNumber}
            </p>
            <p>
              <b>Address:</b> {profile.address}
            </p>
            <p>
              <b>Website:</b> {profile.website}
            </p>
            <p>
              <b>Description:</b> {profile.description}
            </p>
            <p>
              <b>Focus Areas:</b> {(profile.focusAreas || []).join(", ")}
            </p>
            <p>
              <b>Verified:</b> {profile.isVerified ? "Yes" : "No"}
            </p>
            <p>
              <b>Total Received:</b> {profile.totalReceived}
            </p>
            <button onClick={() => setEditProfile(true)}>Edit Profile</button>
          </div>
        )}
        {editProfile && (
          <form onSubmit={handleProfileUpdate}>
            <input
              name="organizationName"
              value={profileForm.organizationName || ""}
              onChange={handleProfileChange}
              placeholder="Organization Name"
              required
            />
            <input
              name="contactPerson"
              value={profileForm.contactPerson || ""}
              onChange={handleProfileChange}
              placeholder="Contact Person"
            />
            <input
              name="phoneNumber"
              value={profileForm.phoneNumber || ""}
              onChange={handleProfileChange}
              placeholder="Phone"
            />
            <input
              name="address"
              value={profileForm.address || ""}
              onChange={handleProfileChange}
              placeholder="Address"
            />
            <input
              name="website"
              value={profileForm.website || ""}
              onChange={handleProfileChange}
              placeholder="Website"
            />
            <input
              name="description"
              value={profileForm.description || ""}
              onChange={handleProfileChange}
              placeholder="Description"
            />
            <input
              name="focusAreas"
              value={(profileForm.focusAreas || []).join(", ")}
              onChange={e =>
                setProfileForm({
                  ...profileForm,
                  focusAreas: e.target.value.split(",").map(s => s.trim()),
                })
              }
              placeholder="Focus Areas (comma separated)"
            />
            <button type="submit">Save</button>
            <button type="button" onClick={() => setEditProfile(false)}>
              Cancel
            </button>
          </form>
        )}
      </section>

      {/* Campaigns */}
      <section style={{ marginBottom: 32 }}>
        <h3>My Campaigns</h3>
        {campaigns.length === 0 ? (
          <p>No campaigns yet.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Description</th>
                <th>Amount</th>
                <th>Volunteer Time</th>
                <th>Approved</th>
                <th>Created</th>
                <th>Expires</th>
                <th>Feedback</th>
              </tr>
            </thead>
            <tbody>
              {campaigns.map((c) => (
                <tr key={c.campaignId || c._id}>
                  <td>{c.description}</td>
                  <td>{c.amount}</td>
                  <td>{c.volunteerTime}</td>
                  <td>{c.approved ? "Yes" : "No"}</td>
                  <td>
                    {c.creationTime
                      ? new Date(c.creationTime).toLocaleString()
                      : ""}
                  </td>
                  <td>
                    {c.expirationTime
                      ? new Date(c.expirationTime).toLocaleString()
                      : ""}
                  </td>
                  <td>{c.feedback || ""}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Volunteer Opportunities */}
      <section style={{ marginBottom: 32 }}>
        <h3>Volunteer Opportunities</h3>
        {opportunities.length === 0 ? (
          <p>No volunteer opportunities yet.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Title</th>
                <th>Description</th>
                <th>Location</th>
                <th>Start</th>
                <th>End</th>
                <th>Max Volunteers</th>
                <th>Current Volunteers</th>
                <th>Skills Required</th>
              </tr>
            </thead>
            <tbody>
              {opportunities.map((o) => (
                <tr key={o.opportunityId || o._id}>
                  <td>{o.title}</td>
                  <td>{o.description}</td>
                  <td>{o.location}</td>
                  <td>
                    {o.startDate
                      ? new Date(o.startDate).toLocaleString()
                      : ""}
                  </td>
                  <td>
                    {o.endDate
                      ? new Date(o.endDate).toLocaleString()
                      : ""}
                  </td>
                  <td>{o.maxVolunteers}</td>
                  <td>{o.currentVolunteers}</td>
                  <td>{(o.skillsRequired || []).join(", ")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Volunteers */}
      <section style={{ marginBottom: 32 }}>
        <h3>Volunteers</h3>
        {volunteers.length === 0 ? (
          <p>No volunteers yet.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Donor ID</th>
                <th>Opportunity ID</th>
                <th>Status</th>
                <th>Hours Committed</th>
                <th>Hours Completed</th>
                <th>Application Date</th>
                <th>Feedback</th>
              </tr>
            </thead>
            <tbody>
              {volunteers.map((v) => (
                <tr key={v.volunteerId || v._id}>
                  <td>{v.donorId}</td>
                  <td>{v.opportunityId}</td>
                  <td>{v.status}</td>
                  <td>{v.hoursCommitted}</td>
                  <td>{v.hoursCompleted}</td>
                  <td>
                    {v.applicationDate
                      ? new Date(v.applicationDate).toLocaleString()
                      : ""}
                  </td>
                  <td>{v.ngoFeedback || ""}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Payments */}
      <section>
        <h3>Donations Received</h3>
        {payments.length === 0 ? (
          <p>No donations received yet.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Donor ID</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((p, idx) => (
                <tr key={p._id || idx}>
                  <td>{p.donorId}</td>
                  <td>{p.amount}</td>
                  <td>{p.status}</td>
                  <td>
                    {p.timestamp
                      ? new Date(p.timestamp).toLocaleString()
                      : ""}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default NGODashboard;
