import React, { useEffect, useState } from "react";
import axios from "axios";

const API_BASE = "/api/admin"; // Adjust if your API base is different

const AdminDashboard = () => {
  const [ngos, setNgos] = useState([]);
  const [donors, setDonors] = useState([]);
  const [campaigns, setCampaigns] = useState([]);
  const [opportunities, setOpportunities] = useState([]);
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);

  const axiosAuth = axios.create({
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
  });

  useEffect(() => {
    fetchAll();
  }, []);

  const fetchAll = async () => {
    setLoading(true);
    await Promise.all([
      fetchNgos(),
      fetchDonors(),
      fetchCampaigns(),
      fetchOpportunities(),
      fetchPayments(),
    ]);
    setLoading(false);
  };

  const fetchNgos = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/ngos`);
      setNgos(res.data || []);
    } catch {
      setNgos([]);
    }
  };

  const fetchDonors = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/donors`);
      setDonors(res.data || []);
    } catch {
      setDonors([]);
    }
  };

  const fetchCampaigns = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/campaigns`);
      setCampaigns(res.data || []);
    } catch {
      setCampaigns([]);
    }
  };

  const fetchOpportunities = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/opportunities`);
      setOpportunities(res.data || []);
    } catch {
      setOpportunities([]);
    }
  };

  const fetchPayments = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/payments`);
      setPayments(res.data || []);
    } catch {
      setPayments([]);
    }
  };

  const handleVerifyNGO = async (ngoId, verify) => {
    try {
      await axiosAuth.patch(`${API_BASE}/ngos/${ngoId}/verify`, { isVerified: verify });
      fetchNgos();
    } catch {
      alert("Failed to update verification status");
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: 1200, margin: "auto", padding: 24 }}>
      <h2>Admin Dashboard</h2>

      {/* NGOs */}
      <section style={{ marginBottom: 32 }}>
        <h3>NGOs</h3>
        {ngos.length === 0 ? (
          <p>No NGOs found.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Organization</th>
                <th>Email</th>
                <th>Registration #</th>
                <th>Contact</th>
                <th>Phone</th>
                <th>Verified</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {ngos.map((ngo) => (
                <tr key={ngo._id || ngo.ngoId}>
                  <td>{ngo.organizationName}</td>
                  <td>{ngo.email}</td>
                  <td>{ngo.registrationNumber}</td>
                  <td>{ngo.contactPerson}</td>
                  <td>{ngo.phoneNumber}</td>
                  <td>{ngo.isVerified ? "Yes" : "No"}</td>
                  <td>
                    {ngo.isVerified ? (
                      <button onClick={() => handleVerifyNGO(ngo._id || ngo.ngoId, false)}>
                        Unverify
                      </button>
                    ) : (
                      <button onClick={() => handleVerifyNGO(ngo._id || ngo.ngoId, true)}>
                        Verify
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Donors */}
      <section style={{ marginBottom: 32 }}>
        <h3>Donors</h3>
        {donors.length === 0 ? (
          <p>No donors found.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Email</th>
                <th>Name</th>
                <th>Phone</th>
                <th>Address</th>
                <th>Occupation</th>
              </tr>
            </thead>
            <tbody>
              {donors.map((d) => (
                <tr key={d._id || d.donorId}>
                  <td>{d.email}</td>
                  <td>{d.firstName} {d.lastName}</td>
                  <td>{d.phoneNumber}</td>
                  <td>{d.address}</td>
                  <td>{d.occupation}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Campaigns */}
      <section style={{ marginBottom: 32 }}>
        <h3>Campaigns</h3>
        {campaigns.length === 0 ? (
          <p>No campaigns found.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Description</th>
                <th>NGO</th>
                <th>Amount</th>
                <th>Volunteer Time</th>
                <th>Approved</th>
                <th>Created</th>
                <th>Expires</th>
              </tr>
            </thead>
            <tbody>
              {campaigns.map((c) => (
                <tr key={c._id || c.campaignId}>
                  <td>{c.description}</td>
                  <td>{c.ngoId}</td>
                  <td>{c.amount}</td>
                  <td>{c.volunteerTime}</td>
                  <td>{c.approved ? "Yes" : "No"}</td>
                  <td>
                    {c.creationTime
                      ? new Date(c.creationTime).toLocaleString()
                      : ""}
                  </td>
                  <td>
                    {c.expirationTime
                      ? new Date(c.expirationTime).toLocaleString()
                      : ""}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Volunteer Opportunities */}
      <section style={{ marginBottom: 32 }}>
        <h3>Volunteer Opportunities</h3>
        {opportunities.length === 0 ? (
          <p>No volunteer opportunities found.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Title</th>
                <th>NGO</th>
                <th>Description</th>
                <th>Location</th>
                <th>Start</th>
                <th>End</th>
                <th>Max Volunteers</th>
                <th>Current Volunteers</th>
              </tr>
            </thead>
            <tbody>
              {opportunities.map((o) => (
                <tr key={o._id || o.opportunityId}>
                  <td>{o.title}</td>
                  <td>{o.ngoId}</td>
                  <td>{o.description}</td>
                  <td>{o.location}</td>
                  <td>
                    {o.startDate
                      ? new Date(o.startDate).toLocaleString()
                      : ""}
                  </td>
                  <td>
                    {o.endDate
                      ? new Date(o.endDate).toLocaleString()
                      : ""}
                  </td>
                  <td>{o.maxVolunteers}</td>
                  <td>{o.currentVolunteers}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Payments */}
      <section>
        <h3>Payments</h3>
        {payments.length === 0 ? (
          <p>No payments found.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Donor ID</th>
                <th>NGO ID</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((p, idx) => (
                <tr key={p._id || idx}>
                  <td>{p.donorId}</td>
                  <td>{p.ngoId}</td>
                  <td>{p.amount}</td>
                  <td>{p.status}</td>
                  <td>
                    {p.timestamp
                      ? new Date(p.timestamp).toLocaleString()
                      : ""}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default AdminDashboard;
