import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './DonorDashboard.css';

const DonorDashboard = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [donationHistory, setDonationHistory] = useState([]);
  const [donorProfile, setDonorProfile] = useState({});
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('campaigns');
  const [donationAmount, setDonationAmount] = useState('');
  const [selectedCampaign, setSelectedCampaign] = useState(null);
  const [showDonationModal, setShowDonationModal] = useState(false);
  const [smsNotifications, setSmsNotifications] = useState([]);
  const [error, setError] = useState('');
  const [donationLoading, setDonationLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const API_BASE_URL = 'http://localhost:8080/api';

  // Enhanced authentication check with debugging
  const checkAuth = useCallback(() => {
    const token = localStorage.getItem('token');
    
    console.log('ðŸ” Auth Check:', {
      hasToken: !!token,
      tokenLength: token?.length,
      tokenPreview: token?.substring(0, 20) + '...'
    });
    
    if (!token) {
      console.log('âŒ No token found');
      setIsAuthenticated(false);
      setLoading(false);
      return false;
    }

    // More flexible token validation
    try {
      // Check if it's a JWT token (has 3 parts separated by dots)
      const tokenParts = token.split('.');
      if (tokenParts.length === 3) {
        console.log('âœ… Valid JWT token format');
        setIsAuthenticated(true);
        return true;
      } else if (token.length > 10) {
        // Accept any token longer than 10 characters (for non-JWT tokens)
        console.log('âœ… Valid token format (non-JWT)');
        setIsAuthenticated(true);
        return true;
      } else {
        console.log('âŒ Invalid token format');
        localStorage.removeItem('token');
        localStorage.removeItem('userRole');
        setIsAuthenticated(false);
        setLoading(false);
        return false;
      }
    } catch (error) {
      console.log('âŒ Token validation error:', error);
      localStorage.removeItem('token');
      localStorage.removeItem('userRole');
      setIsAuthenticated(false);
      setLoading(false);
      return false;
    }
  }, []);

  // Create axios instance with interceptors
  const axiosInstance = axios.create({
    baseURL: API_BASE_URL,
    timeout: 10000,
    headers: {
      'Content-Type': 'application/json'
    }
  });

  // Add token to requests
  axiosInstance.interceptors.request.use(
    (config) => {
      const token = localStorage.getItem('token');
      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
        console.log('ðŸ”‘ Adding token to request:', config.url);
      }
      return config;
    },
    (error) => Promise.reject(error)
  );

  // Handle response errors
  axiosInstance.interceptors.response.use(
    (response) => {
      console.log('âœ… API Response:', response.config.url, response.status);
      return response;
    },
    (error) => {
      console.log('âŒ API Error:', error.config?.url, error.response?.status);
      if (error.response?.status === 401 || error.response?.status === 403) {
        console.log('ðŸš« Authentication failed, clearing tokens');
        localStorage.removeItem('token');
        localStorage.removeItem('userRole');
        setIsAuthenticated(false);
        setError('Session expired. Please login again.');
      }
      return Promise.reject(error);
    }
  );

  // Fetch campaigns using your existing endpoint
  const fetchCampaigns = useCallback(async () => {
    try {
      console.log('ðŸ“Š Fetching campaigns...');
      const response = await axiosInstance.get('/donor/allApproved');
      
      // Your backend returns campaigns directly, not wrapped in ApiResponse
      if (response.data && Array.isArray(response.data)) {
        setCampaigns(response.data);
        console.log('âœ… Campaigns loaded:', response.data.length);
      } else {
        setCampaigns([]);
        console.log('âš ï¸ No campaigns data');
      }
    } catch (error) {
      console.error('âŒ Error fetching campaigns:', error);
      setCampaigns([]);
      if (error.response?.status !== 401) {
        setError('Failed to load campaigns');
      }
    }
  }, [axiosInstance]);

  // Fetch donation history using your existing endpoint
  const fetchDonationHistory = useCallback(async () => {
    try {
      console.log('ðŸ“ˆ Fetching donation history...');
      
      // First get donor profile to get donorId
      const profileResponse = await axiosInstance.get('/donor/profile');
      if (profileResponse.data && profileResponse.data.success) {
        const donorId = profileResponse.data.data.donorId;
        
        // Then fetch donations using donorId
        const donationsResponse = await axiosInstance.get(`/donor/${donorId}`);
        if (donationsResponse.data && Array.isArray(donationsResponse.data)) {
          setDonationHistory(donationsResponse.data);
          console.log('âœ… Donation history loaded:', donationsResponse.data.length);
        } else {
          setDonationHistory([]);
          console.log('âš ï¸ No donation history data');
        }
      }
    } catch (error) {
      console.error('âŒ Error fetching donation history:', error);
      setDonationHistory([]);
      if (error.response?.status !== 401) {
        setError('Failed to load donation history');
      }
    }
  }, [axiosInstance]);

  // Fetch donor profile using your existing endpoint
  const fetchDonorProfile = useCallback(async () => {
    try {
      console.log('ðŸ‘¤ Fetching donor profile...');
      const response = await axiosInstance.get('/donor/profile');
      if (response.data && response.data.success) {
        setDonorProfile(response.data.data || {});
        console.log('âœ… Profile loaded:', response.data.data?.firstName);
      } else {
        setDonorProfile({});
        console.log('âš ï¸ No profile data');
      }
    } catch (error) {
      console.error('âŒ Error fetching donor profile:', error);
      setDonorProfile({});
      if (error.response?.status !== 401) {
        setError('Failed to load profile');
      }
    }
  }, [axiosInstance]);

  const fetchDashboardData = useCallback(async () => {
    try {
      console.log('ðŸ”„ Loading dashboard data...');
      setLoading(true);
      setError('');
      await Promise.all([
        fetchCampaigns(),
        fetchDonationHistory(),
        fetchDonorProfile()
      ]);
      console.log('âœ… Dashboard data loaded successfully');
    } catch (error) {
      console.error('âŒ Error fetching dashboard data:', error);
      setError('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  }, [fetchCampaigns, fetchDonationHistory, fetchDonorProfile]);

  // Check authentication on component mount
  useEffect(() => {
    console.log('ðŸš€ DonorDashboard mounted, checking authentication...');
    
    if (checkAuth()) {
      fetchDashboardData();
    }
  }, [checkAuth, fetchDashboardData]);

  // Handle donation using your existing endpoint structure
  const handleDonation = async () => {
    if (!donationAmount || !selectedCampaign) {
      alert('Please enter donation amount and select a campaign');
      return;
    }

    const amount = parseFloat(donationAmount);
    if (amount < 51) { // Your backend requires >50
      alert('Minimum donation amount is $51');
      return;
    }

    if (isNaN(amount) || amount <= 0) {
      alert('Please enter a valid donation amount');
      return;
    }

    try {
      setDonationLoading(true);
      
      // Structure the request according to your backend expectations
      const donationData = {
        donorId: donorProfile.donorId,
        ngoID: selectedCampaign.ngoId || selectedCampaign.id, // Use appropriate field
        amount: amount
      };

      console.log('ðŸ’° Sending donation request:', donationData);
      const response = await axiosInstance.post('/donor/donate', donationData);
      
      if (response.data && response.data.success) {
        // Generate SMS notification
        const transactionId = `TXN${Date.now()}`;
        const smsMessage = `Thank you for your donation of $${amount.toFixed(2)} to ${selectedCampaign.title}. Transaction ID: ${transactionId}. Your contribution makes a difference!`;
        
        setSmsNotifications(prev => [{
          id: Date.now(),
          message: smsMessage,
          timestamp: new Date(),
          campaignTitle: selectedCampaign.title,
          amount: amount,
          transactionId: transactionId,
          status: 'delivered'
        }, ...prev]);

        alert('Donation successful! SMS notification sent.');
        setShowDonationModal(false);
        setDonationAmount('');
        setSelectedCampaign(null);
        
        // Refresh data
        await Promise.all([fetchDonationHistory(), fetchCampaigns()]);
      } else {
        alert('Donation failed: ' + (response.data?.message || 'Unknown error'));
      }
    } catch (error) {
      console.error('Error processing donation:', error);
      const errorMessage = error.response?.data?.message || 'Error processing donation. Please try again.';
      alert(errorMessage);
    } finally {
      setDonationLoading(false);
    }
  };

  const openDonationModal = (campaign) => {
    setSelectedCampaign(campaign);
    setShowDonationModal(true);
    setDonationAmount('');
  };

  const closeDonationModal = () => {
    setShowDonationModal(false);
    setDonationAmount('');
    setSelectedCampaign(null);
  };

  const handleLogout = () => {
    console.log('ðŸšª Logging out...');
    localStorage.removeItem('token');
    localStorage.removeItem('userRole');
    localStorage.removeItem('userId');
    window.location.href = '/login';
  };

  const handleLogin = () => {
    window.location.href = '/login';
  };

  // Helper function to check if token exists
  const hasToken = () => {
    return localStorage.getItem('token');
  };

  // Debug function to check localStorage
  const debugAuth = () => {
    console.log('ðŸ” Debug Auth State:', {
      token: localStorage.getItem('token'),
      userRole: localStorage.getItem('userRole'),
      userId: localStorage.getItem('userId'),
      isAuthenticated: isAuthenticated,
      loading: loading,
      donorProfile: donorProfile
    });
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      return new Date(dateString).toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return 'Invalid Date';
    }
  };

  const formatCurrency = (amount) => {
    if (amount === null || amount === undefined || isNaN(amount)) return '$0.00';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const calculateProgress = (raised, target) => {
    if (!raised || !target || target === 0) return 0;
    return Math.min((raised / target) * 100, 100);
  };

  const getTotalDonated = () => {
    return donationHistory.reduce((sum, donation) => sum + (donation.amount || 0), 0);
  };

  const clearNotifications = () => {
    setSmsNotifications([]);
  };

  const dismissError = () => {
    setError('');
  };

  // Loading state
  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading your dashboard...</p>
        <button onClick={debugAuth} style={{marginTop: '20px', padding: '10px'}}>
          Debug Auth (Check Console)
        </button>
      </div>
    );
  }

  // Not authenticated state
  if (!isAuthenticated) {
    return (
      <div className="donor-dashboard">
        <div className="dashboard-header">
          <div className="welcome-section">
            <h1>Shohay - DONOR Dashboard</h1>
            <p className="welcome-subtitle">
              Welcome back, {donorProfile?.firstName?.trim() || 'Donor'}! ðŸ‘‹
            </p>
            <button
              className="logout-button"
              onClick={hasToken() ? handleLogout : handleLogin}
            >
              {hasToken() ? 'Logout' : 'Login'}
            </button>
          </div>

          <div className="dashboard-stats">
            <div className="stat-card donations">
              <div className="stat-icon">ðŸ’</div>
              <div className="stat-content">
                <h3>Total Donations</h3>
                <p className="stat-value">{donationHistory?.length || 0}</p>
              </div>
            </div>
            <div className="stat-card contributed">
              <div className="stat-icon">ðŸ’°</div>
              <div className="stat-content">
                <h3>Total Contributed</h3>
                <p className="stat-value">{formatCurrency(getTotalDonated())}</p>
              </div>
            </div>
            <div className="stat-card campaigns">
              <div className="stat-icon">ðŸ“¢</div>
              <div className="stat-content">
                <h3>Active Campaigns</h3>
                <p className="stat-value">{campaigns?.length || 0}</p>
              </div>
            </div>
            <div className="stat-card notifications">
              <div className="stat-icon">ðŸ“±</div>
              <div className="stat-content">
                <h3>SMS Notifications</h3>
                <p className="stat-value">{smsNotifications?.length || 0}</p>
              </div>
            </div>
          </div>
        </div>

        <div className="dashboard-tabs">
          {['campaigns', 'history', 'notifications', 'profile'].map(tab => (
            <button
              key={tab}
              className={`tab-button ${activeTab === tab ? 'active' : ''}`}
              onClick={() => setActiveTab(tab)}
            >
              <span className="tab-icon">
                {tab === 'campaigns' && 'ðŸ“¢'}
                {tab === 'history' && 'ðŸ“Š'}
                {tab === 'notifications' && 'ðŸ“±'}
                {tab === 'profile' && 'ðŸ‘¤'}
              </span>
              {tab === 'campaigns' && 'Available Campaigns'}
              {tab === 'history' && 'Donation History'}
              {tab === 'notifications' && (
                <>
                  SMS Notifications
                  {smsNotifications?.length > 0 && (
                    <span className="notification-badge">{smsNotifications.length}</span>
                  )}
                </>
              )}
              {tab === 'profile' && 'Profile'}
            </button>
          ))}
        </div>
      </div>
    );
  }

  // Authenticated state
  return (
    <div className="donor-dashboard">
      {error && (
        <div className="error-banner">
          <div className="error-content">
            <span className="error-icon">âš ï¸</span>
            <span className="error-text">{error}</span>
          </div>
          <button className="error-dismiss" onClick={dismissError}>Ã—</button>
        </div>
      )}

      <div className="dashboard-header">
        <div className="welcome-section">
          <h1>Shohay - DONOR Dashboard</h1>
          <p className="welcome-subtitle">Welcome back, {donorProfile.firstName || 'Donor'}! ðŸ‘‹</p>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>
        <div className="dashboard-stats">
          <div className="stat-card donations">
            <div className="stat-icon">ðŸ’</div>
            <div className="stat-content">
              <h3>Total Donations</h3>
              <p className="stat-value">{donationHistory.length}</p>
            </div>
          </div>
          <div className="stat-card contributed">
            <div className="stat-icon">ðŸ’°</div>
            <div className="stat-content">
              <h3>Total Contributed</h3>
              <p className="stat-value">{formatCurrency(getTotalDonated())}</p>
            </div>
          </div>
          <div className="stat-card campaigns">
            <div className="stat-icon">ðŸ“¢</div>
            <div className="stat-content">
              <h3>Active Campaigns</h3>
              <p className="stat-value">{campaigns.length}</p>
            </div>
          </div>
          <div className="stat-card notifications">
            <div className="stat-icon">ðŸ“±</div>
            <div className="stat-content">
              <h3>SMS Notifications</h3>
              <p className="stat-value">{smsNotifications.length}</p>
            </div>
          </div>
        </div>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={`tab-button ${activeTab === 'campaigns' ? 'active' : ''}`}
          onClick={() => setActiveTab('campaigns')}
        >
          <span className="tab-icon">ðŸ“¢</span>
          Available Campaigns
        </button>
        <button 
          className={`tab-button ${activeTab === 'history' ? 'active' : ''}`}
          onClick={() => setActiveTab('history')}
        >
          <span className="tab-icon">ðŸ“Š</span>
          Donation History
        </button>
        <button 
          className={`tab-button ${activeTab === 'notifications' ? 'active' : ''}`}
          onClick={() => setActiveTab('notifications')}
        >
          <span className="tab-icon">ðŸ“±</span>
          SMS Notifications
          {smsNotifications.length > 0 && (
            <span className="notification-badge">{smsNotifications.length}</span>
          )}
        </button>
        <button 
          className={`tab-button ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => setActiveTab('profile')}
        >
          <span className="tab-icon">ðŸ‘¤</span>
          Profile
        </button>
      </div>

      <div className="dashboard-content">
        {activeTab === 'campaigns' && (
          <div className="campaigns-section">
            <div className="section-header">
              <h2>ðŸŽ¯ Ongoing Campaigns</h2>
              <button className="refresh-button" onClick={fetchCampaigns}>
                ðŸ”„ Refresh
              </button>
            </div>
            {campaigns.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">ðŸ“¢</div>
                <h3>No Active Campaigns</h3>
                <p>There are no active campaigns available at the moment.</p>
                <p>Check back later for new opportunities to make a difference!</p>
                <button className="refresh-empty-button" onClick={fetchCampaigns}>
                  ðŸ”„ Check Again
                </button>
              </div>
            ) : (
              <div className="campaigns-grid">
                {campaigns.map(campaign => (
                  <div key={campaign.id} className="campaign-card">
                    <div className="campaign-header">
                      <h3>{campaign.title}</h3>
                      <span className="campaign-status active">âœ… Active</span>
                    </div>
                    <p className="campaign-description">{campaign.description}</p>
                    <div className="campaign-progress">
                      <div className="progress-info">
                        <span className="progress-label">Progress</span>
                        <span className="progress-percentage">
                          {Math.round(calculateProgress(campaign.raisedAmount, campaign.targetAmount))}%
                        </span>
                      </div>
                      <div className="progress-bar">
                        <div 
                          className="progress-fill"
                          style={{ width: `${calculateProgress(campaign.raisedAmount, campaign.targetAmount)}%` }}
                        ></div>
                      </div>
                      <div className="progress-text">
                        <span className="raised-amount">
                          {formatCurrency(campaign.raisedAmount || 0)} raised
                        </span>
                        <span className="target-amount">
                          Goal: {formatCurrency(campaign.targetAmount || 0)}
                        </span>
                      </div>
                    </div>
                    <div className="campaign-meta">
                      <div className="meta-item">
                        <span className="meta-label">ðŸ¢ NGO:</span>
                        <span className="meta-value">{campaign.ngoName || 'Unknown NGO'}</span>
                      </div>
                      <div className="meta-item">
                        <span className="meta-label">â° Expires:</span>
                        <span className="meta-value">{formatDate(campaign.expirationTime)}</span>
                      </div>
                      <div className="meta-item">
                        <span className="meta-label">ðŸ“… Created:</span>
                        <span className="meta-value">{formatDate(campaign.creationTime)}</span>
                      </div>
                    </div>
                    <button 
                      className="donate-button"
                      onClick={() => openDonationModal(campaign)}
                    >
                      ðŸ’ Donate Now
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'history' && (
          <div className="history-section">
            <div className="section-header">
              <h2>ðŸ“Š Your Donation History</h2>
              <button className="refresh-button" onClick={fetchDonationHistory}>
                ðŸ”„ Refresh
              </button>
            </div>
            {donationHistory.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">ðŸ’</div>
                <h3>No Donations Yet</h3>
                <p>You haven't made any donations yet.</p>
                <p>Start making a difference today by supporting a campaign!</p>
                <button 
                  className="browse-campaigns-button"
                  onClick={() => setActiveTab('campaigns')}
                >
                  ðŸŽ¯ Browse Campaigns
                </button>
              </div>
            ) : (
              <div className="history-container">
                <div className="history-summary">
                  <div className="summary-card total">
                    <div className="summary-icon">ðŸ’°</div>
                    <div className="summary-content">
                      <h4>Total Donated</h4>
                      <p className="summary-amount">{formatCurrency(getTotalDonated())}</p>
                    </div>
                  </div>
                  <div className="summary-card count">
                    <div className="summary-icon">ðŸ“Š</div>
                    <div className="summary-content">
                      <h4>Number of Donations</h4>
                      <p className="summary-count">{donationHistory.length}</p>
                    </div>
                  </div>
                  <div className="summary-card average">
                    <div className="summary-icon">ðŸ“ˆ</div>
                    <div className="summary-content">
                      <h4>Average Donation</h4>
                      <p className="summary-average">
                        {formatCurrency(getTotalDonated() / donationHistory.length)}
                      </p>
                    </div>
                  </div>
                </div>

                <div className="history-table-container">
                  <table className="history-table">
                    <thead>
                      <tr>
                        <th>ðŸ“… Date</th>
                        <th>ðŸ¢ NGO</th>
                        <th>ðŸ’° Amount</th>
                        <th>ðŸ“‹ Status</th>
                        <th>ðŸ”¢ Transaction ID</th>
                      </tr>
                    </thead>
                    <tbody>
                      {donationHistory?.length > 0 ? (
                        donationHistory.map((donation) => (
                          <tr key={donation.id || `${donation.timestamp}-${donation.amount}`}>
                            <td>{formatDate(donation.timestamp)}</td>
                            <td>{donation.ngoName || donation.ngoId || 'Unknown NGO'}</td>
                            <td className="amount-cell">{formatCurrency(donation.amount)}</td>
                            <td>
                              <span className={`status ${((donation.status || 'SUCCESS').toLowerCase())}`}>
                                {donation.status?.toUpperCase() === 'SUCCESS' ? 'âœ…' : 'âŒ'} {donation.status || 'SUCCESS'}
                              </span>
                            </td>
                            <td className="transaction-id">{donation.transactionId || donation.id || 'N/A'}</td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="5" className="no-data">
                            <div className="empty-state">
                              <div className="empty-icon">ðŸ“‹</div>
                              <h3>No Donation Records Found</h3>
                              <p>Once you make a donation, it will appear here with full details.</p>
                            </div>
                          </td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'notifications' && (
          <div className="notifications-section">
            <div className="section-header">
              <h2>ðŸ“± SMS Notifications</h2>
              <button 
                className="clear-button"
                onClick={clearNotifications}
                disabled={smsNotifications.length === 0}
              >
                ðŸ—‘ï¸ Clear All
              </button>
            </div>
            {smsNotifications.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">ðŸ“±</div>
                <h3>No SMS Notifications</h3>
                <p>You'll receive SMS notifications here when you make donations.</p>
                <p>Notifications include transaction confirmations and receipts.</p>
                <div className="notification-features">
                  <div className="feature">âœ… Instant confirmation</div>
                  <div className="feature">ðŸ“„ Transaction receipts</div>
                  <div className="feature">ðŸ”” Real-time updates</div>
                </div>
              </div>
            ) : (
              <div className="notifications-list">
                {smsNotifications.map(notification => (
                  <div key={notification.id} className="notification-card">
                    <div className="notification-header">
                      <div className="notification-title">
                        <span className="notification-icon">ðŸ“±</span>
                        <span className="notification-text">
                          Donation Confirmation - {notification.campaignTitle}
                        </span>
                        <span className={`notification-status ${notification.status}`}>
                          {notification.status === 'delivered' ? 'âœ…' : 'â³'}
                        </span>
                      </div>
                      <span className="notification-time">
                        {formatDate(notification.timestamp)}
                      </span>
                    </div>
                    <div className="notification-content">
                      <p className="notification-message">{notification.message}</p>
                      <div className="notification-details">
                        <span className="notification-amount">
                          ðŸ’° Amount: {formatCurrency(notification.amount)}
                        </span>
                        <span className="notification-transaction">
                          ðŸ”¢ ID: {notification.transactionId}
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'profile' && (
          <div className="profile-section">
            <h2>ðŸ‘¤ Donor Profile</h2>
            <div className="profile-card">
              <div className="profile-header">
                <div className="profile-avatar">
                  {(donorProfile.firstName || 'D').charAt(0).toUpperCase()}
                </div>
                <div className="profile-info">
                  <h3>{donorProfile.firstName} {donorProfile.lastName}</h3>
                  <p className="profile-email">{donorProfile.email}</p>
                  <span className={`profile-status ${donorProfile.approved ? 'approved' : 'pending'}`}>
                    {donorProfile.approved ? 'âœ… Approved' : 'â³ Pending Approval'}
                  </span>
                </div>
              </div>
              <div className="profile-details">
                <div className="detail-row">
                  <span className="detail-label">ðŸ“ž Phone:</span>
                  <span className="detail-value">{donorProfile.phoneNumber || 'Not provided'}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">ðŸ  Address:</span>
                  <span className="detail-value">{donorProfile.address || 'Not provided'}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">ðŸ’¼ Occupation:</span>
                  <span className="detail-value">{donorProfile.occupation || 'Not provided'}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">ðŸ“… Member Since:</span>
                  <span className="detail-value">{formatDate(donorProfile.registrationDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">ðŸ’° Total Donated:</span>
                  <span className="detail-value">{formatCurrency(donorProfile.totalDonated || 0)}</span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      {/* Donation Modal */}
      {showDonationModal && (
        <div className="modal-overlay" onClick={closeDonationModal}>
          <div className="donation-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>ðŸ’ Make a Donation</h3>
              <button 
                className="close-button"
                onClick={closeDonationModal}
                disabled={donationLoading}
              >
                Ã—
              </button>
            </div>
            <div className="modal-content">
              <div className="campaign-info">
                <h4>{selectedCampaign?.title}</h4>
                <p className="campaign-modal-description">{selectedCampaign?.description}</p>
                <div className="campaign-stats">
                  <div className="stat">
                    <span className="stat-label">ðŸŽ¯ Target:</span>
                    <span className="stat-value">{formatCurrency(selectedCampaign?.targetAmount)}</span>
                  </div>
                  <div className="stat">
                    <span className="stat-label">ðŸ’° Raised:</span>
                    <span className="stat-value">{formatCurrency(selectedCampaign?.raisedAmount)}</span>
                  </div>
                  <div className="stat">
                    <span className="stat-label">ðŸ“Š Remaining:</span>
                    <span className="stat-value">
                      {formatCurrency((selectedCampaign?.targetAmount || 0) - (selectedCampaign?.raisedAmount || 0))}
                    </span>
                  </div>
                </div>
              </div>
              <div className="donation-form">
                <label htmlFor="amount" className="amount-label">
                  ðŸ’° Donation Amount (minimum $51)
                </label>
                <div className="amount-input-container">
                  <span className="currency-symbol">$</span>
                  <input
                    type="number"
                    id="amount"
                    min="51"
                    step="0.01"
                    value={donationAmount}
                    onChange={(e) => setDonationAmount(e.target.value)}
                    placeholder="Enter amount"
                    className="amount-input"
                    disabled={donationLoading}
                  />
                </div>
                <div className="quick-amounts">
                  <button 
                    type="button"
                    onClick={() => setDonationAmount('51')}
                    className={donationAmount === '51' ? 'selected' : ''}
                    disabled={donationLoading}
                  >
                    $51
                  </button>
                  <button 
                    type="button"
                    onClick={() => setDonationAmount('100')}
                    className={donationAmount === '100' ? 'selected' : ''}
                    disabled={donationLoading}
                  >
                    $100
                  </button>
                  <button 
                    type="button"
                    onClick={() => setDonationAmount('250')}
                    className={donationAmount === '250' ? 'selected' : ''}
                    disabled={donationLoading}
                  >
                    $250
                  </button>
                </div>
                {donationAmount && parseFloat(donationAmount) >= 51 && (
                  <div className="donation-impact">
                    <p>âœ¨ Your donation of {formatCurrency(donationAmount)} will make a real difference!</p>
                  </div>
                )}
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="cancel-button"
                onClick={closeDonationModal}
                disabled={donationLoading}
              >
                Cancel
              </button>
              <button 
                className="confirm-donate-button"
                onClick={handleDonation}
                disabled={!donationAmount || parseFloat(donationAmount) < 51 || donationLoading}
              >
                {donationLoading ? (
                  <>
                    <span className="loading-spinner"></span>
                    Processing...
                  </>
                ) : (
                  donationAmount && parseFloat(donationAmount) >= 51 
                    ? `ðŸ’ Donate ${formatCurrency(donationAmount)}` 
                    : 'Enter Amount'
                )}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DonorDashboard;
