// NGORegistration.js - Complete fixed component
import React, { useState } from 'react';
import axios from 'axios';

const NGORegistration = () => {
  const [formData, setFormData] = useState({
    organizationName: '',
    email: '',
    password: '',
    registrationNumber: '',
    contactPerson: '',
    designation: '',
    phoneNumber: '',
    address: '',
    website: '',
    description: '',
    focusAreas: []
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFocusAreaChange = (area) => {
    setFormData(prev => ({
      ...prev,
      focusAreas: prev.focusAreas.includes(area)
        ? prev.focusAreas.filter(item => item !== area)
        : [...prev.focusAreas, area]
    }));
  };

  const validateForm = () => {
    setError('');

    // Required field validation
    if (!formData.organizationName.trim()) {
      setError('Organization name is required');
      return false;
    }
    if (!formData.email.trim()) {
      setError('Email is required');
      return false;
    }
    if (!formData.password.trim()) {
      setError('Password is required');
      return false;
    }
    if (!formData.registrationNumber.trim()) {
      setError('Registration number is required');
      return false;
    }
    if (!formData.contactPerson.trim()) {
      setError('Contact person is required');
      return false;
    }
    if (!formData.phoneNumber.trim()) {
      setError('Phone number is required');
      return false;
    }
    if (formData.focusAreas.length === 0) {
      setError('Please select at least one focus area');
      return false;
    }

    // Email format validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError('Please enter a valid email address');
      return false;
    }

    // Password strength validation
    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters long');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    setError('');

    try {
      console.log('Submitting NGO registration:', formData);

      const response = await axios.post('http://localhost:8080/api/auth/ngo/register', {
        organizationName: formData.organizationName.trim(),
        email: formData.email.trim().toLowerCase(),
        password: formData.password,
        registrationNumber: formData.registrationNumber.trim(),
        contactPerson: formData.contactPerson.trim(),
        designation: formData.designation.trim(),
        phoneNumber: formData.phoneNumber.trim(),
        address: formData.address.trim(),
        website: formData.website.trim(),
        description: formData.description.trim(),
        focusAreas: formData.focusAreas
      }, {
        headers: {
          'Content-Type': 'application/json'
        },
        timeout: 10000
      });

      console.log('Registration response:', response.data);

      if (response.data && response.data.success) {
        alert('NGO registration successful! Please wait for admin approval.');
        
        // Store token if provided
        if (response.data.token) {
          localStorage.setItem('token', response.data.token);
          localStorage.setItem('userRole', 'NGO');
          localStorage.setItem('userId', response.data.data.id);
        }
        
        // Redirect to login or NGO dashboard
        window.location.href = '/login';
      } else {
        setError(response.data?.message || 'Registration failed. Please try again.');
      }
    } catch (error) {
      console.error('Registration error:', error);
      
      if (error.response) {
        const errorMessage = error.response.data?.message || 'Registration failed';
        setError(errorMessage);
      } else if (error.request) {
        setError('Network error. Please check your connection and try again.');
      } else {
        setError('An unexpected error occurred. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="ngo-registration-container">
      <form onSubmit={handleSubmit} className="ngo-registration-form">
        <h2>Register Your NGO</h2>
        
        {error && (
          <div className="error-message">
            <span className="error-icon">⚠️</span>
            {error}
          </div>
        )}

        <div className="form-row">
          <div className="form-group">
            <input
              type="text"
              name="organizationName"
              placeholder="Organization Name *"
              value={formData.organizationName}
              onChange={handleInputChange}
              required
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email Address *"
              value={formData.email}
              onChange={handleInputChange}
              required
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <input
              type="password"
              name="password"
              placeholder="Password (min 6 characters) *"
              value={formData.password}
              onChange={handleInputChange}
              required
              disabled={loading}
              minLength="6"
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              name="registrationNumber"
              placeholder="Registration Number *"
              value={formData.registrationNumber}
              onChange={handleInputChange}
              required
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <input
              type="text"
              name="contactPerson"
              placeholder="Contact Person *"
              value={formData.contactPerson}
              onChange={handleInputChange}
              required
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              name="designation"
              placeholder="Designation"
              value={formData.designation}
              onChange={handleInputChange}
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <input
              type="tel"
              name="phoneNumber"
              placeholder="Phone Number *"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <input
              type="url"
              name="website"
              placeholder="Website (optional)"
              value={formData.website}
              onChange={handleInputChange}
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-group">
          <input
            type="text"
            name="address"
            placeholder="Address"
            value={formData.address}
            onChange={handleInputChange}
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <textarea
            name="description"
            placeholder="Organization Description"
            value={formData.description}
            onChange={handleInputChange}
            disabled={loading}
            rows="4"
          />
        </div>

        <div className="focus-areas-section">
          <h3>Focus Areas (select at least one):</h3>
          <div className="focus-areas-grid">
            {[
              'Education', 'Healthcare', 'Environment', 'Poverty Alleviation',
              'Disaster Relief', 'Animal Welfare', 'Human Rights', 'Technology Access'
            ].map(area => (
              <label key={area} className="focus-area-item">
                <input
                  type="checkbox"
                  checked={formData.focusAreas.includes(area)}
                  onChange={() => handleFocusAreaChange(area)}
                  disabled={loading}
                />
                <span>{area}</span>
              </label>
            ))}
          </div>
        </div>

        <button 
          type="submit" 
          className="register-button"
          disabled={loading}
        >
          {loading ? 'Registering...' : 'Register NGO'}
        </button>

        <p className="login-link">
          Already have an account? <a href="/login">Login here</a>
        </p>
      </form>
    </div>
  );
};

export default NGORegistration;
