  "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80",
  "https://images.unsplash.com/photo-1500534314209-a25ddb2bd429?auto=format&fit=crop&w=400&q=80",
];

// Helper function to get authorization headers
function getAuthHeaders() {
  return {
    Authorization: `Bearer ${localStorage.getItem("token")}`,
    "Content-Type": "application/json",
  };
}

const DonorDashboard = () => {
  const [profile, setProfile] = useState(null);
  const [donorId, setDonorId] = useState(null);
  const [campaigns, setCampaigns] = useState([]);
  const [donations, setDonations] = useState([]);
  const [donateAmount, setDonateAmount] = useState({});
  const [donateLoading, setDonateLoading] = useState({});
  const [donateStatus, setDonateStatus] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch profile and campaigns on component mount
  useEffect(() => {
    fetchProfile();
    fetchCampaigns();
  }, []); // Empty dependency array means this runs once on mount

  // Fetches donor profile and then calls fetchDonations
  const fetchProfile = async () => {
    try {
      const res = await fetch("/api/donor/profile", { headers: getAuthHeaders() });
      if (!res.ok) {
        // Handle specific error codes if needed, e.g., 401 for unauthorized
        if (res.status === 401) {
          setError("Session expired or not logged in. Please log in again.");
          // Optionally redirect to login page
          // window.location.href = "/login";
        } else {
          throw new Error(`Failed to fetch profile: ${res.statusText}`);
        }
      }
      const data = await res.json();
      if (data.success && data.data) { // Assuming your backend wraps data in a 'data' property and includes 'success'
        setProfile(data.data);
        setDonorId(data.data.donorId); // Set donorId from the fetched profile
        fetchDonations(data.data.donorId); // Fetch donations using the obtained donorId
      } else {
        throw new Error(data.message || "Failed to fetch profile data.");
      }
    } catch (err) {
      console.error("Error fetching profile:", err);
      setError("Failed to load profile. " + err.message);
      setLoading(false); // Stop loading even if profile fails
    }
  };

  // Fetches donation history for a specific donor
  const fetchDonations = async (id) => {
    if (!id) {
      console.warn("Donor ID is missing for fetching donations.");
      setLoading(false);
      return;
    }
    try {
      const res = await fetch(`/api/donor/${id}`, { headers: getAuthHeaders() });
      if (!res.ok) {
        throw new Error(`Failed to fetch donations: ${res.statusText}`);
      }
      const data = await res.json();
      // Assuming donation history is an array directly from the response
      setDonations(Array.isArray(data) ? data : data.data || []);
    } catch (err) {
      console.error("Error fetching donation history:", err);
      setError("Failed to load donation history. " + err.message);
    } finally {
      setLoading(false); // Stop loading after donations fetch attempt
    }
  };

  // Fetches all approved campaigns
  const fetchCampaigns = async () => {
    try {
      const res = await fetch("/api/donor/allApproved", { headers: getAuthHeaders() });
      if (!res.ok) {
        throw new Error(`Failed to fetch campaigns: ${res.statusText}`);
      }
      let data = await res.json();
      if (data.success && Array.isArray(data.data)) { // Assuming 'data' is wrapped in 'data' property
        data = data.data; // Get the actual array of campaigns
      } else if (!Array.isArray(data)) { // Fallback if data is not wrapped, but not an array itself
          throw new Error(data.message || "Invalid campaign data format received.");
      }

      // Add made-up names, images, goals, and raised amounts for demo purposes
      data = data.map((c, idx) => ({
        ...c,
        name: [
          "Clean Water for All",
          "Feed the Children",
          "Plant a Million Trees",
          "Education for Every Child",
        ][idx % 4], // Cycle through names
        image: campaignImages[idx % campaignImages.length], // Cycle through images
        goal: c.amount || 10000, // Use existing amount or default to 10000
        raised: Math.floor((c.amount || 10000) * (0.2 + 0.6 * Math.random())), // Random raised amount
      }));
      setCampaigns(data);
    } catch (err) {
      console.error("Error fetching campaigns:", err);
      setError("Failed to load campaigns. " + err.message);
    }
  };

  // Handles change in donation amount input for a specific campaign
  const handleAmountChange = (campaignId, value) => {
    setDonateAmount({ ...donateAmount, [campaignId]: value });
  };

  // Handles the donation process for a campaign
  const handleDonate = async (campaign) => {
    const amount = parseFloat(donateAmount[campaign.campaignId]);
    if (isNaN(amount) || amount < 50) {
      setDonateStatus({ ...donateStatus, [campaign.campaignId]: "Amount must be at least 50" });
      return;
    }

    setDonateLoading({ ...donateLoading, [campaign.campaignId]: true });
    setDonateStatus({ ...donateStatus, [campaign.campaignId]: "" }); // Clear previous status

    const payload = {
      donorId,
      ngoID: campaign.ngoEmail, // Assuming ngoEmail is used as ngoID in your backend
      amount,
    };

    try {
      const res = await fetch("/api/donor/donate", {
        method: "POST",
        headers: getAuthHeaders(),
        body: JSON.stringify(payload),
      });

      const data = await res.json();
      if (res.ok && data.success) {
        setDonateStatus({ ...donateStatus, [campaign.campaignId]: "Donation successful!" });
        // Refresh donation history and campaigns to reflect the new donation
        fetchDonations(donorId);
        fetchCampaigns(); // To update raised amounts
      } else {
        throw new Error(data.message || "Donation failed.");
      }
    } catch (err) {
      console.error("Error during donation:", err);
      setDonateStatus({ ...donateStatus, [campaign.campaignId]: err.message });
    } finally {
      setDonateLoading({ ...donateLoading, [campaign.campaignId]: false });
    }
  };

  // Display loading indicator
  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '20px' }}>
        Loading Donor Dashboard...
      </div>
    );
  }

  return (
    <div style={{ maxWidth: 1100, margin: "auto", padding: 24, fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <h2 style={{ textAlign: "center", marginBottom: 30, color: "#333" }}>Donor Dashboard</h2>

      {/* Error Message */}
      {error && (
        <div style={{ color: "red", backgroundColor: "#ffe0e0", border: "1px solid red", padding: 12, borderRadius: 8, marginBottom: 24 }}>
          <strong>Error:</strong> {error}
        </div>
      )}

      {/* Profile Section */}
      <section style={{ marginBottom: 40, padding: 20, border: "1px solid #e0e0e0", borderRadius: 10, backgroundColor: "#fff" }}>
        <h3 style={{ borderBottom: "1px solid #eee", paddingBottom: 10, marginBottom: 20, color: "#555" }}>My Profile</h3>
        {profile ? (
          <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "10px 20px" }}>
            <p>
              <b>Name:</b> {profile.firstName} {profile.lastName}
            </p>
            <p>
              <b>Email:</b> {profile.email}
            </p>
            <p>
              <b>Phone:</b> {profile.phoneNumber}
            </p>
            <p>
              <b>Address:</b> {profile.address}
            </p>
            <p>
              <b>Occupation:</b> {profile.occupation}
            </p>
          </div>
        ) : (
          <p style={{ color: "#777" }}>Profile information not available. Please ensure you are logged in.</p>
        )}
      </section>

      {/* Available Campaigns Section */}
      <section style={{ marginBottom: 40 }}>
        <h3 style={{ borderBottom: "1px solid #eee", paddingBottom: 10, marginBottom: 20, color: "#555" }}>Available Campaigns</h3>
        <div style={{ display: "flex", flexWrap: "wrap", gap: 24, justifyContent: campaigns.length === 0 ? "center" : "flex-start" }}>
          {campaigns.length === 0 ? (
            <p style={{ color: "#777", fontSize: 16 }}>No active campaigns available at the moment. Check back later!</p>
          ) : (
            campaigns.map((c, idx) => {
              const percent = Math.min(100, Math.round((c.raised / c.goal) * 100));
              return (
                <div
                  key={c.campaignId || idx} // Use campaignId if available, fallback to index
                  style={{
                    width: 320, // Slightly wider cards
                    border: "1px solid #ddd",
                    borderRadius: 12,
                    padding: 18,
                    background: "#ffffff",
                    boxShadow: "0 4px 12px rgba(0,0,0,0.08)",
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "space-between",
                  }}
                >
                  <img
                    src={c.image}
                    alt={c.name}
                    style={{ width: "100%", height: 160, objectFit: "cover", borderRadius: 8, marginBottom: 15 }}
                  />
                  <h4 style={{ margin: "0 0 8px", color: "#333", fontSize: 18 }}>{c.name}</h4>
                  <div style={{ fontSize: 14, color: "#666", marginBottom: 15, flexGrow: 1 }}>{c.description || "No description provided."}</div>

                  {/* Progress Bar */}
                  <div style={{ marginBottom: 15 }}>
                    <div style={{ fontSize: 13, color: "#888", display: "flex", justifyContent: "space-between", marginBottom: 4 }}>
                      <span>Goal: ${c.goal.toLocaleString()}</span>
                      <span>Raised: ${c.raised.toLocaleString()}</span>
                    </div>
                    <div
                      style={{
                        background: "#e0e0e0",
                        borderRadius: 8,
                        height: 18,
                        overflow: "hidden",
                      }}
                    >
                      <div
                        style={{
                          width: `${percent}%`,
                          background: percent < 100 ? "#4CAF50" : "#2196F3", // Green for ongoing, Blue for complete
                          height: "100%",
                          transition: "width 0.5s ease-in-out",
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "flex-end",
                          paddingRight: 5,
                          color: "#fff",
                          fontSize: 12,
                          fontWeight: "bold",
                        }}
                      >
                        {percent > 5 && `${percent}%`} {/* Show percentage only if it's visible */}
                      </div>
                    </div>
                    <div style={{ fontSize: 12, color: "#666", textAlign: "right", marginTop: 5 }}>
                      {percent}% funded
                    </div>
                  </div>

                  {/* Donate Input and Button */}
                  <div style={{ marginTop: 15, display: "flex", alignItems: "center" }}>
                    <input
                      type="number"
                      min={50}
                      placeholder="Amount (min 50)"
                      value={donateAmount[c.campaignId] || ""}
                      onChange={e => handleAmountChange(c.campaignId, e.target.value)}
                      style={{
                        width: 120,
                        marginRight: 10,
                        padding: "8px 12px",
                        border: "1px solid #ccc",
                        borderRadius: 6,
                        fontSize: 14,
                      }}
                    />
                    <button
                      disabled={donateLoading[c.campaignId] || !donorId} // Disable if donating or donorId is not set
                      onClick={() => handleDonate(c)}
                      style={{
                        padding: "8px 15px",
                        backgroundColor: "#007bff",
                        color: "white",
                        border: "none",
                        borderRadius: 6,
                        cursor: donorLoading[c.campaignId] || !donorId ? "not-allowed" : "pointer",
                        opacity: donorLoading[c.campaignId] || !donorId ? 0.6 : 1,
                        fontSize: 14,
                      }}
                    >
                      {donateLoading[c.campaignId] ? "Donating..." : "Donate"}
                    </button>
                  </div>
                  {donateStatus[c.campaignId] && (
                    <div style={{
                      color: donateStatus[c.campaignId].includes("successful") ? "green" : "red",
                      fontSize: 13,
                      marginTop: 10,
                      textAlign: "center"
                    }}>
                      {donateStatus[c.campaignId]}
                    </div>
                  )}
                </div>
              );
            })
          )}
        </div>
      </section>

      {/* Donation History Section */}
      <section>
        <h3 style={{ borderBottom: "1px solid #eee", paddingBottom: 10, marginBottom: 20, color: "#555" }}>My Donation History</h3>
        {donations.length === 0 ? (
          <p style={{ color: "#777", fontSize: 16 }}>You haven't made any donations yet.</p>
        ) : (
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              background: "#fff",
              borderRadius: 10,
              overflow: "hidden",
              boxShadow: "0 4px 12px rgba(0,0,0,0.08)",
            }}
          >
            <thead>
              <tr style={{ background: "#f8f8f8", borderBottom: "1px solid #eee" }}>
                <th style={{ padding: "12px 15px", textAlign: "left", color: "#333", fontSize: 15 }}>NGO ID</th>
                <th style={{ padding: "12px 15px", textAlign: "left", color: "#333", fontSize: 15 }}>Amount</th>
                <th style={{ padding: "12px 15px", textAlign: "left", color: "#333", fontSize: 15 }}>Status</th>
                <th style={{ padding: "12px 15px", textAlign: "left", color: "#333", fontSize: 15 }}>Date</th>
              </tr>
            </thead>
            <tbody>
              {donations.map((d, idx) => (
                <tr key={d._id || idx} style={{ borderBottom: "1px solid #f5f5f5" }}>
                  <td style={{ padding: "10px 15px", color: "#555" }}>{d.ngoId || "N/A"}</td>
                  <td style={{ padding: "10px 15px", color: "#555" }}>${(d.amount || 0).toLocaleString()}</td>
                  <td style={{ padding: "10px 15px", color: "#555" }}>{d.status || "Pending"}</td>
                  <td style={{ padding: "10px 15px", color: "#555" }}>
                    {d.timestamp
                      ? new Date(d.timestamp).toLocaleString()
                      : "N/A"}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default DonorDashboard;
