import React, { useEffect, useState } from "react";

const DonorDashboard = () => {
  const [profile, setProfile] = useState(null);
  const [donations, setDonations] = useState([]);
  const [volunteers, setVolunteers] = useState([]);
  const [campaigns, setCampaigns] = useState([]);
  const [loading, setLoading] = useState(true);

  // Helper to get auth headers
  const getAuthHeaders = () => ({
    Authorization: `Bearer ${localStorage.getItem("token")}`,
    "Content-Type": "application/json",
  });

  // Fetch donor profile
  useEffect(() => {
    const fetchProfile = async () => {
      const res = await fetch("/api/donor/profile", {
        headers: getAuthHeaders(),
      });
      if (res.ok) {
        const data = await res.json();
        setProfile(data.data);
        fetchDonations(data.data.donorId);
      }
    };
    fetchProfile();
    fetchVolunteers();
    fetchCampaigns();
  }, []);

  // Fetch donation history
  const fetchDonations = async (donorId) => {
    const res = await fetch(`/api/donor/${donorId}`, {
      headers: getAuthHeaders(),
    });
    if (res.ok) {
      const data = await res.json();
      setDonations(data);
    }
    setLoading(false);
  };

  // Fetch volunteer history
  const fetchVolunteers = async () => {
    const res = await fetch("/api/donor/volunteers", {
      headers: getAuthHeaders(),
    });
    if (res.ok) {
      const data = await res.json();
      setVolunteers(data.data || []);
    }
  };

  // Fetch all approved campaigns
  const fetchCampaigns = async () => {
    const res = await fetch("/api/donor/allApproved", {
      headers: getAuthHeaders(),
    });
    if (res.ok) {
      const data = await res.json();
      setCampaigns(data || []);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: 900, margin: "auto", padding: 24 }}>
      <h2>Donor Dashboard</h2>

      {/* Profile Section */}
      <section style={{ marginBottom: 32 }}>
        <h3>Profile</h3>
        {profile ? (
          <div>
            <p>
              <b>Name:</b> {profile.firstName} {profile.lastName}
            </p>
            <p>
              <b>Email:</b> {profile.email}
            </p>
            <p>
              <b>Phone:</b> {profile.phoneNumber}
            </p>
            <p>
              <b>Address:</b> {profile.address}
            </p>
            <p>
              <b>Occupation:</b> {profile.occupation}
            </p>
          </div>
        ) : (
          <p>Profile not found.</p>
        )}
      </section>

      {/* Donation History */}
      <section style={{ marginBottom: 32 }}>
        <h3>Donation History</h3>
        {donations.length === 0 ? (
          <p>No donations yet.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>NGO ID</th>
                <th>Amount</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {donations.map((d, idx) => (
                <tr key={d.paymentId || d._id || idx}>
                  <td>{d.ngoId}</td>
                  <td>{d.amount}</td>
                  <td>{d.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Volunteer History */}
      <section style={{ marginBottom: 32 }}>
        <h3>Volunteer History</h3>
        {volunteers.length === 0 ? (
          <p>No volunteer applications yet.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Opportunity ID</th>
                <th>Status</th>
                <th>Hours Committed</th>
                <th>Hours Completed</th>
              </tr>
            </thead>
            <tbody>
              {volunteers.map((v) => (
                <tr key={v.volunteerId || v._id}>
                  <td>{v.opportunityId}</td>
                  <td>{v.status}</td>
                  <td>{v.hoursCommitted}</td>
                  <td>{v.hoursCompleted}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Approved Campaigns */}
      <section>
        <h3>Approved Campaigns</h3>
        {campaigns.length === 0 ? (
          <p>No approved campaigns available.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Description</th>
                <th>Expires</th>
                <th>Volunteer Time</th>
              </tr>
            </thead>
            <tbody>
              {campaigns.map((c) => (
                <tr key={c.campaignId || c._id}>
                  <td>{c.description}</td>
                  <td>
                    {c.expirationTime
                      ? new Date(c.expirationTime).toLocaleString()
                      : "No expiry"}
                  </td>
                  <td>{c.volunteerTime || "N/A"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default DonorDashboard;
