import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [ngos, setNgos] = useState([]);
  const [donors, setDonors] = useState([]);
  const [campaigns, setCampaigns] = useState([]);
  const [unapprovedCampaigns, setUnapprovedCampaigns] = useState([]);
  const [volunteers, setVolunteers] = useState([]);
  const [payments, setPayments] = useState([]);
  const [adminProfile, setAdminProfile] = useState({});
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [error, setError] = useState('');
  const [actionLoading, setActionLoading] = useState({});

  const API_BASE_URL = 'http://localhost:8080/api';
  const token = localStorage.getItem('token');

  // Create axios instance with interceptors
  const axiosInstance = axios.create({
    baseURL: API_BASE_URL,
    timeout: 10000,
    headers: {
      'Content-Type': 'application/json'
    }
  });

  // Add token to requests
  axiosInstance.interceptors.request.use(
    (config) => {
      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
      }
      return config;
    },
    (error) => Promise.reject(error)
  );

  // Handle response errors
  axiosInstance.interceptors.response.use(
    (response) => response,
    (error) => {
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        window.location.href = '/login';
      }
      return Promise.reject(error);
    }
  );

  const fetchAdminProfile = useCallback(async () => {
    try {
      const response = await axiosInstance.get('/admin/profile');
      if (response.data.success) {
        setAdminProfile(response.data.data || {});
      }
    } catch (error) {
      console.error('Error fetching admin profile:', error);
    }
  }, []);

  const fetchUnapprovedCampaigns = useCallback(async () => {
    try {
      const response = await axiosInstance.get('/admin/campaigns/unapproved');
      if (response.data.success) {
        setUnapprovedCampaigns(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching unapproved campaigns:', error);
    }
  }, []);

  const fetchAllCampaigns = useCallback(async () => {
    try {
      const response = await axiosInstance.get('/admin/campaigns');
      if (response.data.success) {
        setCampaigns(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching campaigns:', error);
    }
  }, []);

  const fetchNgos = useCallback(async () => {
    try {
      const response = await axiosInstance.get('/admin/ngos');
      if (response.data.success) {
        setNgos(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching NGOs:', error);
    }
  }, []);

  const fetchDonors = useCallback(async () => {
    try {
      const response = await axiosInstance.get('/admin/donors');
      if (response.data.success) {
        setDonors(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching donors:', error);
    }
  }, []);

  const fetchVolunteers = useCallback(async () => {
    try {
      const response = await axiosInstance.get('/admin/volunteers');
      if (response.data.success) {
        setVolunteers(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching volunteers:', error);
    }
  }, []);

  const fetchPayments = useCallback(async () => {
    try {
      const response = await axiosInstance.get('/admin/payments');
      if (response.data.success) {
        setPayments(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching payments:', error);
    }
  }, []);

  const fetchDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      await Promise.all([
        fetchAdminProfile(),
        fetchUnapprovedCampaigns(),
        fetchAllCampaigns(),
        fetchNgos(),
        fetchDonors(),
        fetchVolunteers(),
        fetchPayments()
      ]);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setError('Failed to load dashboard data. Please refresh the page.');
    } finally {
      setLoading(false);
    }
  }, [fetchAdminProfile, fetchUnapprovedCampaigns, fetchAllCampaigns, fetchNgos, fetchDonors, fetchVolunteers, fetchPayments]);

  useEffect(() => {
    if (token) {
      fetchDashboardData();
    } else {
      setError('Please login to access admin dashboard');
      setLoading(false);
    }
  }, [fetchDashboardData, token]);

  const handleCampaignAction = async (campaignId, action) => {
    try {
      setActionLoading(prev => ({ ...prev, [campaignId]: true }));
      
      const endpoint = action === 'approve' 
        ? `/admin/campaigns/${campaignId}/approve`
        : `/admin/campaigns/${campaignId}/reject`;
      
      const response = await axiosInstance.post(endpoint);
      
      if (response.data.success) {
        alert(`Campaign ${action}d successfully!`);
        await Promise.all([fetchUnapprovedCampaigns(), fetchAllCampaigns()]);
      } else {
        alert(`Failed to ${action} campaign: ${response.data.message}`);
      }
    } catch (error) {
      console.error(`Error ${action}ing campaign:`, error);
      alert(`Error ${action}ing campaign. Please try again.`);
    } finally {
      setActionLoading(prev => ({ ...prev, [campaignId]: false }));
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      return new Date(dateString).toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return 'Invalid Date';
    }
  };

  const formatCurrency = (amount) => {
    if (amount === null || amount === undefined || isNaN(amount)) return '$0.00';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getTotalDonations = () => {
    return payments.reduce((sum, payment) => sum + (payment.amount || 0), 0);
  };

  const getApprovedCampaigns = () => {
    return campaigns.filter(campaign => campaign.approved).length;
  };

  const getVerifiedNgos = () => {
    return ngos.filter(ngo => ngo.isVerified).length;
  };

  const dismissError = () => {
    setError('');
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading admin dashboard...</p>
      </div>
    );
  }

  if (error && !token) {
    return (
      <div className="dashboard-error">
        <div className="error-icon">🔒</div>
        <h2>Access Denied</h2>
        <p>Please login as admin to access this dashboard.</p>
        <button 
          className="login-redirect-button"
          onClick={() => window.location.href = '/login'}
        >
          Go to Login
        </button>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      {error && (
        <div className="error-banner">
          <div className="error-content">
            <span className="error-icon">⚠️</span>
            <span className="error-text">{error}</span>
          </div>
          <button className="error-dismiss" onClick={dismissError}>×</button>
        </div>
      )}

      <div className="dashboard-header">
        <div className="welcome-section">
          <h1>Admin Dashboard 👨‍💼</h1>
          <p className="welcome-subtitle">Welcome back, {adminProfile.firstName || 'Admin'}! Manage your platform effectively.</p>
        </div>
        <div className="dashboard-stats">
          <div className="stat-card total-ngos">
            <div className="stat-icon">🏢</div>
            <div className="stat-content">
              <h3>Total NGOs</h3>
              <p className="stat-value">{ngos.length}</p>
              <p className="stat-detail">{getVerifiedNgos()} verified</p>
            </div>
          </div>
          <div className="stat-card total-donors">
            <div className="stat-icon">👥</div>
            <div className="stat-content">
              <h3>Total Donors</h3>
              <p className="stat-value">{donors.length}</p>
              <p className="stat-detail">Active users</p>
            </div>
          </div>
          <div className="stat-card total-campaigns">
            <div className="stat-icon">🎯</div>
            <div className="stat-content">
              <h3>Campaigns</h3>
              <p className="stat-value">{campaigns.length}</p>
              <p className="stat-detail">{getApprovedCampaigns()} approved</p>
            </div>
          </div>
          <div className="stat-card total-donations">
            <div className="stat-icon">💰</div>
            <div className="stat-content">
              <h3>Total Donations</h3>
              <p className="stat-value">{formatCurrency(getTotalDonations())}</p>
              <p className="stat-detail">{payments.length} transactions</p>
            </div>
          </div>
        </div>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => setActiveTab('overview')}
        >
          <span className="tab-icon">📊</span>
          Overview
        </button>
        <button 
          className={`tab-button ${activeTab === 'campaigns' ? 'active' : ''}`}
          onClick={() => setActiveTab('campaigns')}
        >
          <span className="tab-icon">🎯</span>
          Campaign Approval
          {unapprovedCampaigns.length > 0 && (
            <span className="notification-badge">{unapprovedCampaigns.length}</span>
          )}
        </button>
        <button 
          className={`tab-button ${activeTab === 'ngos' ? 'active' : ''}`}
          onClick={() => setActiveTab('ngos')}
        >
          <span className="tab-icon">🏢</span>
          NGOs
        </button>
        <button 
          className={`tab-button ${activeTab === 'donors' ? 'active' : ''}`}
          onClick={() => setActiveTab('donors')}
        >
          <span className="tab-icon">👥</span>
          Donors
        </button>
        <button 
          className={`tab-button ${activeTab === 'payments' ? 'active' : ''}`}
          onClick={() => setActiveTab('payments')}
        >
          <span className="tab-icon">💳</span>
          Payments
        </button>
      </div>

      <div className="dashboard-content">
        {activeTab === 'overview' && (
          <div className="overview-section">
            <h2>📊 Platform Overview</h2>
            <div className="overview-grid">
              <div className="overview-card">
                <h3>🎯 Recent Campaigns</h3>
                <div className="recent-items">
                  {campaigns.slice(0, 5).map(campaign => (
                    <div key={campaign.id} className="recent-item">
                      <div className="item-info">
                        <span className="item-title">{campaign.title}</span>
                        <span className="item-meta">{campaign.ngoName}</span>
                      </div>
                      <span className={`item-status ${campaign.approved ? 'approved' : 'pending'}`}>
                        {campaign.approved ? '✅' : '⏳'}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
              
              <div className="overview-card">
                <h3>💰 Recent Donations</h3>
                <div className="recent-items">
                  {payments.slice(0, 5).map(payment => (
                    <div key={payment.id} className="recent-item">
                      <div className="item-info">
                        <span className="item-title">{formatCurrency(payment.amount)}</span>
                        <span className="item-meta">{formatDate(payment.timestamp)}</span>
                      </div>
                      <span className={`item-status ${payment.status.toLowerCase()}`}>
                        {payment.status === 'COMPLETED' ? '✅' : '⏳'}
                      </span>
                    </div>
                  ))}
                </div>
              </div>

              <div className="overview-card">
                <h3>🏢 NGO Status</h3>
                <div className="status-breakdown">
                  <div className="status-item">
                    <span className="status-label">Verified NGOs</span>
                    <span className="status-value">{getVerifiedNgos()}</span>
                  </div>
                  <div className="status-item">
                    <span className="status-label">Pending Verification</span>
                    <span className="status-value">{ngos.length - getVerifiedNgos()}</span>
                  </div>
                  <div className="status-item">
                    <span className="status-label">Total NGOs</span>
                    <span className="status-value">{ngos.length}</span>
                  </div>
                </div>
              </div>

              <div className="overview-card">
                <h3>📈 Platform Metrics</h3>
                <div className="metrics">
                  <div className="metric-item">
                    <span className="metric-label">Average Donation</span>
                    <span className="metric-value">
                      {formatCurrency(payments.length > 0 ? getTotalDonations() / payments.length : 0)}
                    </span>
                  </div>
                  <div className="metric-item">
                    <span className="metric-label">Active Campaigns</span>
                    <span className="metric-value">{getApprovedCampaigns()}</span>
                  </div>
                  <div className="metric-item">
                    <span className="metric-label">Success Rate</span>
                    <span className="metric-value">
                      {campaigns.length > 0 ? Math.round((getApprovedCampaigns() / campaigns.length) * 100) : 0}%
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'campaigns' && (
          <div className="campaigns-section">
            <div className="section-header">
              <h2>🎯 Campaign Approval</h2>
              <button className="refresh-button" onClick={fetchUnapprovedCampaigns}>
                🔄 Refresh
              </button>
            </div>
            
            {unapprovedCampaigns.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">✅</div>
                <h3>All Caught Up!</h3>
                <p>No campaigns pending approval at the moment.</p>
              </div>
            ) : (
              <div className="campaigns-grid">
                {unapprovedCampaigns.map(campaign => (
                  <div key={campaign.id} className="campaign-approval-card">
                    <div className="campaign-header">
                      <h3>{campaign.title}</h3>
                      <span className="campaign-status pending">⏳ Pending</span>
                    </div>
                    <p className="campaign-description">{campaign.description}</p>
                    <div className="campaign-details">
                      <div className="detail-item">
                        <span className="detail-label">🏢 NGO:</span>
                        <span className="detail-value">{campaign.ngoName}</span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">🎯 Target:</span>
                        <span className="detail-value">{formatCurrency(campaign.targetAmount)}</span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">📅 Created:</span>
                        <span className="detail-value">{formatDate(campaign.creationTime)}</span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">⏰ Expires:</span>
                        <span className="detail-value">{formatDate(campaign.expirationTime)}</span>
                      </div>
                    </div>
                    <div className="campaign-actions">
                      <button 
                        className="approve-button"
                        onClick={() => handleCampaignAction(campaign.id, 'approve')}
                        disabled={actionLoading[campaign.id]}
                      >
                        {actionLoading[campaign.id] ? '⏳ Processing...' : '✅ Approve'}
                      </button>
                      <button 
                        className="reject-button"
                        onClick={() => handleCampaignAction(campaign.id, 'reject')}
                        disabled={actionLoading[campaign.id]}
                      >
                        {actionLoading[campaign.id] ? '⏳ Processing...' : '❌ Reject'}
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'ngos' && (
          <div className="ngos-section">
            <div className="section-header">
              <h2>🏢 NGO Management</h2>
              <button className="refresh-button" onClick={fetchNgos}>
                🔄 Refresh
              </button>
            </div>
            
            {ngos.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">🏢</div>
                <h3>No NGOs Found</h3>
                <p>No NGOs have registered yet.</p>
              </div>
            ) : (
              <div className="table-container">
                <table className="data-table">
                  <thead>
                    <tr>
                      <th>🏢 Organization</th>
                      <th>📧 Email</th>
                      <th>📋 Registration #</th>
                      <th>👤 Contact Person</th>
                      <th>📞 Phone</th>
                      <th>✅ Verified</th>
                      <th>📅 Registered</th>
                    </tr>
                  </thead>
                  <tbody>
                    {ngos.map(ngo => (
                      <tr key={ngo.id}>
                        <td className="org-name">{ngo.organizationName}</td>
                        <td>{ngo.email}</td>
                        <td className="reg-number">{ngo.registrationNumber}</td>
                        <td>{ngo.contactPerson}</td>
                        <td>{ngo.phoneNumber}</td>
                        <td>
                          <span className={`verification-status ${ngo.isVerified ? 'verified' : 'unverified'}`}>
                            {ngo.isVerified ? '✅ Verified' : '⏳ Pending'}
                          </span>
                        </td>
                        <td>{formatDate(ngo.registrationDate)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        )}

        {activeTab === 'donors' && (
          <div className="donors-section">
            <div className="section-header">
              <h2>👥 Donor Management</h2>
              <button className="refresh-button" onClick={fetchDonors}>
                🔄 Refresh
              </button>
            </div>
            
            {donors.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">👥</div>
                <h3>No Donors Found</h3>
                <p>No donors have registered yet.</p>
              </div>
            ) : (
              <div className="table-container">
                <table className="data-table">
                  <thead>
                    <tr>
                      <th>👤 Name</th>
                      <th>📧 Email</th>
                      <th>📞 Phone</th>
                      <th>🏠 Address</th>
                      <th>🔄 Frequency</th>
                      <th>✅ Status</th>
                      <th>📅 Registered</th>
                    </tr>
                  </thead>
                  <tbody>
                    {donors.map(donor => (
                      <tr key={donor.id}>
                        <td className="donor-name">{donor.firstName} {donor.lastName}</td>
                        <td>{donor.email}</td>
                        <td>{donor.phoneNumber || 'N/A'}</td>
                        <td>{donor.address || 'N/A'}</td>
                        <td>{donor.donationFrequency || 'N/A'}</td>
                        <td>
                          <span className={`donor-status ${donor.approved ? 'approved' : 'pending'}`}>
                            {donor.approved ? '✅ Approved' : '⏳ Pending'}
                          </span>
                        </td>
                        <td>{formatDate(donor.registrationDate)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        )}

        {activeTab === 'payments' && (
          <div className="payments-section">
            <div className="section-header">
              <h2>💳 Payment Transactions</h2>
              <button className="refresh-button" onClick={fetchPayments}>
                🔄 Refresh
              </button>
            </div>
            
            {payments.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">💳</div>
                <h3>No Payments Found</h3>
                <p>No payment transactions have been made yet.</p>
              </div>
            ) : (
              <div className="payments-container">
                <div className="payments-summary">
                  <div className="summary-card">
                    <h4>💰 Total Amount</h4>
                    <p className="summary-value">{formatCurrency(getTotalDonations())}</p>
                  </div>
                  <div className="summary-card">
                    <h4>📊 Total Transactions</h4>
                    <p className="summary-value">{payments.length}</p>
                  </div>
                  <div className="summary-card">
                    <h4>📈 Average Transaction</h4>
                    <p className="summary-value">
                      {formatCurrency(payments.length > 0 ? getTotalDonations() / payments.length : 0)}
                    </p>
                  </div>
                </div>
                
                <div className="table-container">
                  <table className="data-table">
                    <thead>
                      <tr>
                        <th>🔢 Transaction ID</th>
                        <th>👤 Donor</th>
                        <th>🏢 NGO</th>
                        <th>💰 Amount</th>
                        <th>📋 Status</th>
                        <th>📅 Date</th>
                      </tr>
                    </thead>
                    <tbody>
                      {payments.map(payment => (
                        <tr key={payment.id}>
                          <td className="transaction-id">{payment.id}</td>
                          <td>{payment.donorName || payment.donorId}</td>
                          <td>{payment.ngoName || payment.ngoId}</td>
                          <td className="amount-cell">{formatCurrency(payment.amount)}</td>
                          <td>
                            <span className={`payment-status ${payment.status.toLowerCase()}`}>
                              {payment.status === 'COMPLETED' ? '✅' : '⏳'} {payment.status}
                            </span>
                          </td>
                          <td>{formatDate(payment.timestamp)}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
