import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './DonorDashboard.css';

const DonorDashboard = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [donationHistory, setDonationHistory] = useState([]);
  const [donorProfile, setDonorProfile] = useState({});
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('campaigns');
  const [donationAmount, setDonationAmount] = useState('');
  const [selectedCampaign, setSelectedCampaign] = useState(null);
  const [showDonationModal, setShowDonationModal] = useState(false);
  const [smsNotifications, setSmsNotifications] = useState([]);
  const [error, setError] = useState('');
  const [donationLoading, setDonationLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const API_BASE_URL = 'http://localhost:8080/api';

  // Create axios instance with interceptors
  const createAxiosInstance = useCallback(() => {
    const instance = axios.create({
      baseURL: API_BASE_URL,
      timeout: 10000,
      headers: {
        'Content-Type': 'application/json',
      },
    });

    // Add token to requests
    instance.interceptors.request.use(
      (config) => {
        const token = localStorage.getItem('token');
        if (token) {
          config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
      },
      (error) => Promise.reject(error)
    );

    // Handle response errors
    instance.interceptors.response.use(
      (response) => response,
      (error) => {
        if (error.response?.status === 401 || error.response?.status === 403) {
          console.log('Unauthorized or Forbidden, redirecting to login');
          localStorage.removeItem('token');
          localStorage.removeItem('userRole');
          localStorage.removeItem('userId');
          setIsAuthenticated(false);
          setError('Session expired. Redirecting to login...');
          setTimeout(() => {
            window.location.href = '/login';
          }, 2000); // Redirect after 2 seconds
        }
        return Promise.reject(error);
      }
    );

    return instance;
  }, []);

  const fetchCampaigns = useCallback(async () => {
    try {
      const axiosInstance = createAxiosInstance();
      const response = await axiosInstance.get('/donor/campaigns');
      if (response.data && response.data.success) {
        setCampaigns(response.data.data || []);
      } else {
        setCampaigns([]);
      }
    } catch (error) {
      console.error('Error fetching campaigns:', error.message);
      setCampaigns([]);
      if (error.response?.status !== 401 && error.response?.status !== 403) {
        setError('Failed to load campaigns');
      }
    }
  }, [createAxiosInstance]);

  const fetchDonationHistory = useCallback(async () => {
    try {
      const axiosInstance = createAxiosInstance();
      const response = await axiosInstance.get('/donor/donations');
      if (response.data && response.data.success) {
        setDonationHistory(response.data.data || []);
      } else {
        setDonationHistory([]);
      }
    } catch (error) {
      console.error('Error fetching donation history:', error.message);
      setDonationHistory([]);
      if (error.response?.status !== 401 && error.response?.status !== 403) {
        setError('Failed to load donation history');
      }
    }
  }, [createAxiosInstance]);

  const fetchDonorProfile = useCallback(async () => {
    try {
      const axiosInstance = createAxiosInstance();
      const response = await axiosInstance.get('/donor/profile');
      if (response.data && response.data.success) {
        setDonorProfile(response.data.data || {});
      } else {
        setDonorProfile({});
      }
    } catch (error) {
      console.error('Error fetching donor profile:', error.message);
      setDonorProfile({});
      if (error.response?.status !== 401 && error.response?.status !== 403) {
        setError('Failed to load profile');
      }
    }
  }, [createAxiosInstance]);

  const fetchDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      await Promise.all([
        fetchCampaigns(),
        fetchDonationHistory(),
        fetchDonorProfile(),
      ]);
    } catch (error) {
      console.error('Error fetching dashboard data:', error.message);
      setError('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  }, [fetchCampaigns, fetchDonationHistory, fetchDonorProfile]);

  // Check authentication on component mount
  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      console.log('Checking token:', token); // Debug log

      if (!token) {
        console.log('No token found, redirecting to login');
        setIsAuthenticated(false);
        setError('No session found. Redirecting to login...');
        setTimeout(() => {
          window.location.href = '/login';
        }, 2000); // Redirect after 2 seconds
        setLoading(false);
        return;
      }

      // Rely on backend to validate token
      setIsAuthenticated(true);
      fetchDashboardData();
    };

    checkAuth();
  }, [fetchDashboardData]);

  const handleDonation = async () => {
    if (!donationAmount || !selectedCampaign) {
      alert('Please enter donation amount and select a campaign');
      return;
    }

    const amount = parseFloat(donationAmount);
    if (amount < 10) {
      alert('Minimum donation amount is $10');
      return;
    }

    if (isNaN(amount) || amount <= 0) {
      alert('Please enter a valid donation amount');
      return;
    }

    try {
      setDonationLoading(true);
      const axiosInstance = createAxiosInstance();
      const donationData = {
        campaignId: selectedCampaign.id,
        amount: amount,
        ngoId: selectedCampaign.ngoId,
      };

      const response = await axiosInstance.post('/donor/donate', donationData);

      if (response.data && response.data.success) {
        const transactionId = response.data.data?.id || `TXN${Date.now()}`;
        const smsMessage = `Thank you for your donation of $${amount.toFixed(2)} to ${selectedCampaign.title}. Transaction ID: ${transactionId}. Your contribution makes a difference!`;

        setSmsNotifications((prev) => [
          {
            id: Date.now(),
            message: smsMessage,
            timestamp: new Date(),
            campaignTitle: selectedCampaign.title,
            amount: amount,
            transactionId: transactionId,
            status: 'delivered',
          },
          ...prev,
        ]);

        alert('Donation successful! SMS notification sent.');
        setShowDonationModal(false);
        setDonationAmount('');
        setSelectedCampaign(null);

        await Promise.all([fetchDonationHistory(), fetchCampaigns()]);
      } else {
        alert('Donation failed: ' + (response.data?.message || 'Unknown error'));
      }
    } catch (error) {
      console.error('Error processing donation:', error.message);
      const errorMessage = error.response?.data?.message || 'Error processing donation. Please try again.';
      alert(errorMessage);
    } finally {
      setDonationLoading(false);
    }
  };

  const openDonationModal = (campaign) => {
    setSelectedCampaign(campaign);
    setShowDonationModal(true);
    setDonationAmount('');
  };

  const closeDonationModal = () => {
    setShowDonationModal(false);
    setDonationAmount('');
    setSelectedCampaign(null);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userRole');
    localStorage.removeItem('userId');
    setIsAuthenticated(false);
    window.location.href = '/login';
  };

  const handleLogin = () => {
    window.location.href = '/login';
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      return new Date(dateString).toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
      });
    } catch (error) {
      return 'Invalid Date';
    }
  };

  const formatCurrency = (amount) => {
    if (amount === null || amount === undefined || isNaN(amount)) return '$0.00';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  const calculateProgress = (raised, target) => {
    if (!raised || !target || target === 0) return 0;
    return Math.min((raised / target) * 100, 100);
  };

  const getTotalDonated = () => {
    return donationHistory.reduce((sum, donation) => sum + (donation.amount || 0), 0);
  };

  const clearNotifications = () => {
    setSmsNotifications([]);
  };

  const dismissError = () => {
    setError('');
  };

  // Loading state
  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  // Not authenticated state
  if (!isAuthenticated) {
    return (
      <div className="donor-dashboard">
        <div className="dashboard-header">
          <div className="welcome-section">
            <h1>Shohay - DONOR Dashboard</h1>
            <button className="logout-button" onClick={handleLogin}>
              Login
            </button>
          </div>
        </div>
        
        <div className="dashboard-error">
          <div className="error-icon">🔒</div>
          <h2>Access Denied</h2>
          <p>{error || 'Please login to access your donor dashboard.'}</p>
          <button 
            className="login-redirect-button"
            onClick={handleLogin}
          >
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  // Authenticated state
  return (
    <div className="donor-dashboard">
      {error && (
        <div className="error-banner">
          <div className="error-content">
            <span className="error-icon">⚠️</span>
            <span className="error-text">{error}</span>
          </div>
          <button className="error-dismiss" onClick={dismissError}>×</button>
        </div>
      )}

      <div className="dashboard-header">
        <div className="welcome-section">
          <h1>Shohay - DONOR Dashboard</h1>
          <p className="welcome-subtitle">Welcome back, {donorProfile.firstName || 'Donor'}! 👋</p>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>
        <div className="dashboard-stats">
          <div className="stat-card donations">
            <div className="stat-icon">💝</div>
            <div className="stat-content">
              <h3>Total Donations</h3>
              <p className="stat-value">{donationHistory.length}</p>
            </div>
          </div>
          <div className="stat-card contributed">
            <div className="stat-icon">💰</div>
            <div className="stat-content">
              <h3>Total Contributed</h3>
              <p className="stat-value">{formatCurrency(getTotalDonated())}</p>
            </div>
          </div>
          <div className="stat-card campaigns">
            <div className="stat-icon">📢</div>
            <div className="stat-content">
              <h3>Active Campaigns</h3>
              <p className="stat-value">{campaigns.length}</p>
            </div>
          </div>
          <div className="stat-card notifications">
            <div className="stat-icon">📱</div>
            <div className="stat-content">
              <h3>SMS Notifications</h3>
              <p className="stat-value">{smsNotifications.length}</p>
            </div>
          </div>
        </div>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={`tab-button ${activeTab === 'campaigns' ? 'active' : ''}`}
          onClick={() => setActiveTab('campaigns')}
        >
          <span className="tab-icon">📢</span>
          Available Campaigns
        </button>
        <button 
          className={`tab-button ${activeTab === 'history' ? 'active' : ''}`}
          onClick={() => setActiveTab('history')}
        >
          <span className="tab-icon">📊</span>
          Donation History
        </button>
        <button 
          className={`tab-button ${activeTab === 'notifications' ? 'active' : ''}`}
          onClick={() => setActiveTab('notifications')}
        >
          <span className="tab-icon">📱</span>
          SMS Notifications
          {smsNotifications.length > 0 && (
            <span className="notification-badge">{smsNotifications.length}</span>
          )}
        </button>
        <button 
          className={`tab-button ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => setActiveTab('profile')}
        >
          <span className="tab-icon">👤</span>
          Profile
        </button>
      </div>

      <div className="dashboard-content">
        {activeTab === 'campaigns' && (
          <div className="campaigns-section">
            <div className="section-header">
              <h2>🎯 Ongoing Campaigns</h2>
              <button className="refresh-button" onClick={fetchCampaigns}>
                🔄 Refresh
              </button>
            </div>
            {campaigns.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">📢</div>
                <h3>No Active Campaigns</h3>
                <p>There are no active campaigns available at the moment.</p>
                <p>Check back later for new opportunities to make a difference!</p>
                <button className="refresh-empty-button" onClick={fetchCampaigns}>
                  🔄 Check Again
                </button>
              </div>
            ) : (
              <div className="campaigns-grid">
                {campaigns.map(campaign => (
                  <div key={campaign.id} className="campaign-card">
                    <div className="campaign-header">
                      <h3>{campaign.title}</h3>
                      <span className="campaign-status active">✅ Active</span>
                    </div>
                    <p className="campaign-description">{campaign.description}</p>
                    <div className="campaign-progress">
                      <div className="progress-info">
                        <span className="progress-label">Progress</span>
                        <span className="progress-percentage">
                          {Math.round(calculateProgress(campaign.raisedAmount, campaign.targetAmount))}%
                        </span>
                      </div>
                      <div className="progress-bar">
                        <div 
                          className="progress-fill"
                          style={{ width: `${calculateProgress(campaign.raisedAmount, campaign.targetAmount)}%` }}
                        ></div>
                      </div>
                      <div className="progress-text">
                        <span className="raised-amount">
                          {formatCurrency(campaign.raisedAmount || 0)} raised
                        </span>
                        <span className="target-amount">
                          Goal: {formatCurrency(campaign.targetAmount || 0)}
                        </span>
                      </div>
                    </div>
                    <div className="campaign-meta">
                      <div className="meta-item">
                        <span className="meta-label">🏢 NGO:</span>
                        <span className="meta-value">{campaign.ngoName || 'Unknown NGO'}</span>
                      </div>
                      <div className="meta-item">
                        <span className="meta-label">⏰ Expires:</span>
                        <span className="meta-value">{formatDate(campaign.expirationTime)}</span>
                      </div>
                      <div className="meta-item">
                        <span className="meta-label">📅 Created:</span>
                        <span className="meta-value">{formatDate(campaign.creationTime)}</span>
                      </div>
                    </div>
                    <button 
                      className="donate-button"
                      onClick={() => openDonationModal(campaign)}
                    >
                      💝 Donate Now
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'history' && (
          <div className="history-section">
            <div className="section-header">
              <h2>📊 Your Donation History</h2>
              <button className="refresh-button" onClick={fetchDonationHistory}>
                🔄 Refresh
              </button>
            </div>
            {donationHistory.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">💝</div>
                <h3>No Donations Yet</h3>
                <p>You haven't made any donations yet.</p>
                <p>Start making a difference today by supporting a campaign!</p>
                <button 
                  className="browse-campaigns-button"
                  onClick={() => setActiveTab('campaigns')}
                >
                  🎯 Browse Campaigns
                </button>
              </div>
            ) : (
              <div className="history-container">
                <div className="history-summary">
                  <div className="summary-card total">
                    <div className="summary-icon">💰</div>
                    <div className="summary-content">
                      <h4>Total Donated</h4>
                      <p className="summary-amount">{formatCurrency(getTotalDonated())}</p>
                    </div>
                  </div>
                  <div className="summary-card count">
                    <div className="summary-icon">📊</div>
                    <div className="summary-content">
                      <h4>Number of Donations</h4>
                      <p className="summary-count">{donationHistory.length}</p>
                    </div>
                  </div>
                  <div className="summary-card average">
                    <div className="summary-icon">📈</div>
                    <div className="summary-content">
                      <h4>Average Donation</h4>
                      <p className="summary-average">
                        {formatCurrency(getTotalDonated() / donationHistory.length)}
                      </p>
                    </div>
                  </div>
                </div>
                <div className="history-table-container">
                  <table className="history-table">
                    <thead>
                      <tr>
                        <th>📅 Date</th>
                        <th>🎯 Campaign</th>
                        <th>🏢 NGO</th>
                        <th>💰 Amount</th>
                        <th>📋 Status</th>
                        <th>🔢 Transaction ID</th>
                      </tr>
                    </thead>
                    <tbody>
                      {donationHistory.map(donation => (
                        <tr key={donation.id}>
                          <td>{formatDate(donation.donationDate)}</td>
                          <td className="campaign-title">{donation.campaignTitle || 'N/A'}</td>
                          <td>{donation.ngoName || 'N/A'}</td>
                          <td className="amount-cell">{formatCurrency(donation.amount)}</td>
                          <td>
                            <span className={`status ${(donation.status || 'completed').toLowerCase()}`}>
                              {donation.status === 'completed' ? '✅' : '⏳'} {donation.status || 'Completed'}
                            </span>
                          </td>
                          <td className="transaction-id">{donation.id}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'notifications' && (
          <div className="notifications-section">
            <div className="section-header">
              <h2>📱 SMS Notifications</h2>
              <button 
                className="clear-button"
                onClick={clearNotifications}
                disabled={smsNotifications.length === 0}
              >
                🗑️ Clear All
              </button>
            </div>
            {smsNotifications.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">📱</div>
                <h3>No SMS Notifications</h3>
                <p>You'll receive SMS notifications here when you make donations.</p>
                <p>Notifications include transaction confirmations and receipts.</p>
                <div className="notification-features">
                  <div className="feature">✅ Instant confirmation</div>
                  <div className="feature">📄 Transaction receipts</div>
                  <div className="feature">🔔 Real-time updates</div>
                </div>
              </div>
            ) : (
              <div className="notifications-list">
                {smsNotifications.map(notification => (
                  <div key={notification.id} className="notification-card">
                    <div className="notification-header">
                      <div className="notification-title">
                        <span className="notification-icon">📱</span>
                        <span className="notification-text">
                          Donation Confirmation - {notification.campaignTitle}
                        </span>
                        <span className={`notification-status ${notification.status}`}>
                          {notification.status === 'delivered' ? '✅' : '⏳'}
                        </span>
                      </div>
                      <span className="notification-time">
                        {formatDate(notification.timestamp)}
                      </span>
                    </div>
                    <div className="notification-content">
                      <p className="notification-message">{notification.message}</p>
                      <div className="notification-details">
                        <span className="notification-amount">
                          💰 Amount: {formatCurrency(notification.amount)}
                        </span>
                        <span className="notification-transaction">
                          🔢 ID: {notification.transactionId}
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'profile' && (
          <div className="profile-section">
            <h2>👤 Donor Profile</h2>
            <div className="profile-card">
              <div className="profile-header">
                <div className="profile-avatar">
                  {(donorProfile.firstName || 'D').charAt(0).toUpperCase()}
                </div>
                <div className="profile-info">
                  <h3>{donorProfile.firstName} {donorProfile.lastName}</h3>
                  <p className="profile-email">{donorProfile.email}</p>
                  <span className={`profile-status ${donorProfile.approved ? 'approved' : 'pending'}`}>
                    {donorProfile.approved ? '✅ Approved' : '⏳ Pending Approval'}
                  </span>
                </div>
              </div>
              <div className="profile-details">
                <div className="detail-row">
                  <span className="detail-label">📞 Phone:</span>
                  <span className="detail-value">{donorProfile.phoneNumber || 'Not provided'}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">🏠 Address:</span>
                  <span className="detail-value">{donorProfile.address || 'Not provided'}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">📅 Member Since:</span>
                  <span className="detail-value">{formatDate(donorProfile.registrationDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">🔄 Donation Frequency:</span>
                  <span className="detail-value">{donorProfile.donationFrequency || 'Not specified'}</span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Donation Modal */}
      {showDonationModal && (
        <div className="modal-overlay" onClick={closeDonationModal}>
          <div className="donation-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>💝 Make a Donation</h3>
              <button 
                className="close-button"
                onClick={closeDonationModal}
                disabled={donationLoading}
              >
                ×
              </button>
            </div>
            <div className="modal-content">
              <div className="campaign-info">
                <h4>{selectedCampaign?.title}</h4>
                <p className="campaign-modal-description">{selectedCampaign?.description}</p>
                <div className="campaign-stats">
                  <div className="stat">
                    <span className="stat-label">🎯 Target:</span>
                    <span className="stat-value">{formatCurrency(selectedCampaign?.targetAmount)}</span>
                  </div>
                  <div className="stat">
                    <span className="stat-label">💰 Raised:</span>
                    <span className="stat-value">{formatCurrency(selectedCampaign?.raisedAmount)}</span>
                  </div>
                  <div className="stat">
                    <span className="stat-label">📊 Remaining:</span>
                    <span className="stat-value">
                      {formatCurrency((selectedCampaign?.targetAmount || 0) - (selectedCampaign?.raisedAmount || 0))}
                    </span>
                  </div>
                </div>
              </div>
              <div className="donation-form">
                <label htmlFor="amount" className="amount-label">
                  💰 Donation Amount (minimum $10)
                </label>
                <div className="amount-input-container">
                  <span className="currency-symbol">$</span>
                  <input
                    type="number"
                    id="amount"
                    min="10"
                    step="0.01"
                    value={donationAmount}
                    onChange={(e) => setDonationAmount(e.target.value)}
                    placeholder="Enter amount"
                    className="amount-input"
                    disabled={donationLoading}
                  />
                </div>
                <div className="quick-amounts">
                  <button 
                    type="button"
                    onClick={() => setDonationAmount('25')}
                    className={donationAmount === '25' ? 'selected' : ''}
                    disabled={donationLoading}
                  >
                    $25
                  </button>
                  <button 
                    type="button"
                    onClick={() => setDonationAmount('50')}
                    className={donationAmount === '50' ? 'selected' : ''}
                    disabled={donationLoading}
                  >
                    $50
                  </button>
                  <button 
                    type="button"
                    onClick={() => setDonationAmount('100')}
                    className={donationAmount === '100' ? 'selected' : ''}
                    disabled={donationLoading}
                  >
                    $100
                  </button>
                  <button 
                    type="button"
                    onClick={() => setDonationAmount('250')}
                    className={donationAmount === '250' ? 'selected' : ''}
                    disabled={donationLoading}
                  >
                    $250
                  </button>
                </div>
                {donationAmount && parseFloat(donationAmount) >= 10 && (
                  <div className="donation-impact">
                    <p>✨ Your donation of {formatCurrency(donationAmount)} will make a real difference!</p>
                  </div>
                )}
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="cancel-button"
                onClick={closeDonationModal}
                disabled={donationLoading}
              >
                Cancel
              </button>
              <button 
                className="confirm-donate-button"
                onClick={handleDonation}
                disabled={!donationAmount || parseFloat(donationAmount) < 10 || donationLoading}
              >
                {donationLoading ? (
                  <>
                    <span className="loading-spinner"></span>
                    Processing...
                  </>
                ) : (
                  donationAmount && parseFloat(donationAmount) >= 10 
                    ? `💝 Donate ${formatCurrency(donationAmount)}` 
                    : 'Enter Amount'
                )}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DonorDashboard;
