import React, { useState } from 'react';
import axios from 'axios';
import '../../styles/Auth.css';

const RegisterDonor = ({ onSwitchToLogin }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    address: '',
    occupation: '',
    focusAreas: [],
    donationFrequency: '',
    preferredAmount: ''
  });
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const focusOptions = [
    'Education', 'Healthcare', 'Environment', 'Poverty Alleviation',
    'Disaster Relief', 'Animal Welfare', 'Human Rights', 'Technology Access'
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox') {
      setFormData(prev => ({
        ...prev,
        focusAreas: checked
          ? [...prev.focusAreas, value]
          : prev.focusAreas.filter(area => area !== value)
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const validateForm = () => {
    // Check required fields
    if (!formData.firstName.trim()) {
      setMessage('First name is required');
      return false;
    }
    if (!formData.lastName.trim()) {
      setMessage('Last name is required');
      return false;
    }
    if (!formData.email.trim()) {
      setMessage('Email is required');
      return false;
    }
    if (!formData.password) {
      setMessage('Password is required');
      return false;
    }
    if (formData.password !== formData.confirmPassword) {
      setMessage('Passwords do not match');
      return false;
    }
    if (!formData.phone.trim()) {
      setMessage('Phone number is required');
      return false;
    }
    if (!formData.address.trim()) {
      setMessage('Address is required');
      return false;
    }
    if (!formData.donationFrequency) {
      setMessage('Please select donation frequency');
      return false;
    }

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setMessage('Please enter a valid email address');
      return false;
    }

    // Password strength
    if (formData.password.length < 6) {
      setMessage('Password must be at least 6 characters long');
      return false;
    }

    // Phone validation (more flexible)
    const phonePattern = /^(\+88)?01[3-9]\d{8}$/;
    if (!phonePattern.test(formData.phone.replace(/\s/g, ''))) {
      setMessage('Phone number must be in valid Bangladeshi format (01XXXXXXXXX)');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);
    setMessage('');

    try {
      // Prepare data for backend (matching your backend DTO)
      const donorData = {
        firstName: formData.firstName.trim(),
        lastName: formData.lastName.trim(),
        email: formData.email.trim().toLowerCase(),
        password: formData.password,
        phoneNumber: formData.phone.trim(), // Backend expects 'phoneNumber'
        address: formData.address.trim(),
        donationFrequency: formData.donationFrequency
        // Note: Your backend DTO doesn't include occupation, focusAreas, preferredAmount
      };

      console.log('Sending donor registration data:', donorData);

      const response = await axios.post('http://localhost:8080/api/auth/donor/register', donorData, {
        headers: {
          'Content-Type': 'application/json'
        },
        timeout: 10000
      });

      console.log('Registration response:', response.data);

      if (response.data && response.data.success) {
        setMessage('Registration successful! Welcome to Shohay!');
        
        // Store authentication data if token is provided
        if (response.data.token) {
          localStorage.setItem('token', response.data.token);
          localStorage.setItem('userRole', 'DONOR');
          localStorage.setItem('userId', response.data.data.donorId);
          
          // Redirect to donor dashboard
          setTimeout(() => {
            window.location.href = '/donor/dashboard';
          }, 2000);
        } else {
          // Redirect to login if no token
          setTimeout(() => {
            if (onSwitchToLogin) {
              onSwitchToLogin();
            } else {
              window.location.href = '/login';
            }
          }, 2000);
        }
      } else {
        setMessage(response.data?.message || 'Registration failed. Please try again.');
      }
    } catch (error) {
      console.error('Registration error:', error);
      
      if (error.response) {
        const errorMessage = error.response.data?.message || 'Registration failed';
        setMessage(errorMessage);
      } else if (error.request) {
        setMessage('Network error. Please check your connection and try again.');
      } else {
        setMessage('An unexpected error occurred. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h2>Register as Donor</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <input
            type="text"
            name="firstName"
            placeholder="First Name *"
            value={formData.firstName}
            onChange={handleChange}
            required
            disabled={isLoading}
          />
          <input
            type="text"
            name="lastName"
            placeholder="Last Name *"
            value={formData.lastName}
            onChange={handleChange}
            required
            disabled={isLoading}
          />
        </div>

        <input
          type="email"
          name="email"
          placeholder="Email *"
          value={formData.email}
          onChange={handleChange}
          required
          disabled={isLoading}
        />

        <div className="form-row">
          <input
            type="password"
            name="password"
            placeholder="Password (min 6 chars) *"
            value={formData.password}
            onChange={handleChange}
            required
            disabled={isLoading}
            minLength="6"
          />
          <input
            type="password"
            name="confirmPassword"
            placeholder="Confirm Password *"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
            disabled={isLoading}
          />
        </div>

        <input
          type="tel"
          name="phone"
          placeholder="Phone Number (01XXXXXXXXX) *"
          value={formData.phone}
          onChange={handleChange}
          required
          disabled={isLoading}
        />

        <textarea
          name="address"
          placeholder="Address *"
          value={formData.address}
          onChange={handleChange}
          required
          disabled={isLoading}
          rows="3"
        />

        <input
          type="text"
          name="occupation"
          placeholder="Occupation (optional)"
          value={formData.occupation}
          onChange={handleChange}
          disabled={isLoading}
        />

        <div className="focus-areas">
          <label>Areas of Interest (optional):</label>
          <div className="focus-checkboxes">
            {focusOptions.map(area => (
              <label key={area} className="checkbox-label">
                <input
                  type="checkbox"
                  value={area}
                  checked={formData.focusAreas.includes(area)}
                  onChange={handleChange}
                  disabled={isLoading}
                />
                {area}
              </label>
            ))}
          </div>
        </div>

        <select
          name="donationFrequency"
          value={formData.donationFrequency}
          onChange={handleChange}
          required
          disabled={isLoading}
        >
          <option value="">Select Donation Frequency *</option>
          <option value="One-time">One-time</option>
          <option value="Monthly">Monthly</option>
          <option value="Quarterly">Quarterly</option>
          <option value="Annually">Annually</option>
        </select>

        <input
          type="number"
          name="preferredAmount"
          placeholder="Preferred Donation Amount (BDT) - optional"
          value={formData.preferredAmount}
          onChange={handleChange}
          min="1"
          disabled={isLoading}
        />

        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Registering...' : 'Register as Donor'}
        </button>
      </form>

      {message && (
        <div className={`auth-message ${message.includes('successful') ? 'success' : 'error'}`}>
          {message}
        </div>
      )}

      <p>
        Already have an account?{' '}
        <span className="auth-link" onClick={onSwitchToLogin}>
          Login here
        </span>
      </p>
    </div>
  );
};

export default RegisterDonor;
