import React, { useEffect, useState } from "react";
import axios from "axios";

const API_BASE = "/api/donor"; // Adjust if your API base is different

const DonorDashboard = () => {
  const [profile, setProfile] = useState(null);
  const [editProfile, setEditProfile] = useState(false);
  const [profileForm, setProfileForm] = useState({});
  const [donations, setDonations] = useState([]);
  const [volunteers, setVolunteers] = useState([]);
  const [campaigns, setCampaigns] = useState([]);
  const [loading, setLoading] = useState(true);

  // For donation form
  const [donateModal, setDonateModal] = useState({ open: false, campaign: null });
  const [donateAmount, setDonateAmount] = useState("");
  const [donateStatus, setDonateStatus] = useState(null);

  // Assume token is stored in localStorage
  const axiosAuth = axios.create({
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
  });

  useEffect(() => {
    Promise.all([
      fetchProfile(),
      fetchVolunteers(),
      fetchCampaigns()
    ]).then(() => setLoading(false));
    // fetchDonations is called after profile is loaded (need donorId)
  }, []);

  const fetchProfile = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/profile`);
      setProfile(res.data.data);
      setProfileForm(res.data.data);
      fetchDonations(res.data.data.donorId);
    } catch (err) {
      alert("Failed to load profile");
    }
  };

  const fetchDonations = async (donorId) => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/${donorId}`);
      setDonations(res.data);
    } catch (err) {
      setDonations([]);
    }
  };

  const fetchVolunteers = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/volunteers`);
      setVolunteers(res.data.data || []);
    } catch (err) {
      setVolunteers([]);
    }
  };

  const fetchCampaigns = async () => {
    try {
      const res = await axiosAuth.get(`${API_BASE}/allApproved`);
      setCampaigns(res.data || []);
    } catch (err) {
      setCampaigns([]);
    }
  };

  const handleProfileChange = (e) => {
    setProfileForm({ ...profileForm, [e.target.name]: e.target.value });
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await axiosAuth.put(`${API_BASE}/profile`, profileForm);
      setProfile(res.data.data);
      setEditProfile(false);
      alert("Profile updated!");
    } catch (err) {
      alert("Failed to update profile");
    }
  };

  // --- Donation logic ---
  const openDonateModal = (campaign) => {
    setDonateModal({ open: true, campaign });
    setDonateAmount("");
    setDonateStatus(null);
  };

  const closeDonateModal = () => {
    setDonateModal({ open: false, campaign: null });
    setDonateAmount("");
    setDonateStatus(null);
  };

  const handleDonate = async (e) => {
    e.preventDefault();
    setDonateStatus("processing");
    try {
      // Assuming backend expects: { donorId, ngoId, amount, campaignId }
      await axiosAuth.post(`${API_BASE}/donate`, {
        donorId: profile.donorId,
        ngoId: donateModal.campaign.ngoId,
        amount: Number(donateAmount),
        campaignId: donateModal.campaign.campaignId || donateModal.campaign._id,
      });
      setDonateStatus("success");
      fetchDonations(profile.donorId); // Refresh donation history
    } catch (err) {
      setDonateStatus("error");
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: 900, margin: "auto", padding: 24 }}>
      <h2>Donor Dashboard</h2>

      {/* Profile Section */}
      <section style={{ marginBottom: 32 }}>
        <h3>Profile</h3>
        {profile && !editProfile && (
          <div>
            <p>
              <b>Name:</b> {profile.firstName} {profile.lastName}
            </p>
            <p>
              <b>Email:</b> {profile.email}
            </p>
            <p>
              <b>Phone:</b> {profile.phoneNumber}
            </p>
            <p>
              <b>Address:</b> {profile.address}
            </p>
            <p>
              <b>Occupation:</b> {profile.occupation}
            </p>
            <button onClick={() => setEditProfile(true)}>Edit Profile</button>
          </div>
        )}
        {editProfile && (
          <form onSubmit={handleProfileUpdate}>
            <input
              name="firstName"
              value={profileForm.firstName || ""}
              onChange={handleProfileChange}
              placeholder="First Name"
              required
            />
            <input
              name="lastName"
              value={profileForm.lastName || ""}
              onChange={handleProfileChange}
              placeholder="Last Name"
              required
            />
            <input
              name="phoneNumber"
              value={profileForm.phoneNumber || ""}
              onChange={handleProfileChange}
              placeholder="Phone"
            />
            <input
              name="address"
              value={profileForm.address || ""}
              onChange={handleProfileChange}
              placeholder="Address"
            />
            <input
              name="occupation"
              value={profileForm.occupation || ""}
              onChange={handleProfileChange}
              placeholder="Occupation"
            />
            <button type="submit">Save</button>
            <button type="button" onClick={() => setEditProfile(false)}>
              Cancel
            </button>
          </form>
        )}
      </section>

      {/* Donation History */}
      <section style={{ marginBottom: 32 }}>
        <h3>Donation History</h3>
        {donations.length === 0 ? (
          <p>No donations yet.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>NGO ID</th>
                <th>Campaign ID</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {donations.map((d) => (
                <tr key={d.paymentId || d._id}>
                  <td>{d.ngoId}</td>
                  <td>{d.campaignId || ""}</td>
                  <td>{d.amount}</td>
                  <td>{d.status}</td>
                  <td>{d.timestamp ? new Date(d.timestamp).toLocaleString() : ""}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Volunteer History */}
      <section style={{ marginBottom: 32 }}>
        <h3>Volunteer History</h3>
        {volunteers.length === 0 ? (
          <p>No volunteer applications yet.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Opportunity ID</th>
                <th>Status</th>
                <th>Hours Committed</th>
                <th>Hours Completed</th>
              </tr>
            </thead>
            <tbody>
              {volunteers.map((v) => (
                <tr key={v.volunteerId || v._id}>
                  <td>{v.opportunityId}</td>
                  <td>{v.status}</td>
                  <td>{v.hoursCommitted}</td>
                  <td>{v.hoursCompleted}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Approved Campaigns */}
      <section>
        <h3>Approved Campaigns</h3>
        {campaigns.length === 0 ? (
          <p>No approved campaigns available.</p>
        ) : (
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Description</th>
                <th>NGO ID</th>
                <th>Expires</th>
                <th>Volunteer Time</th>
                <th>Donate</th>
              </tr>
            </thead>
            <tbody>
              {campaigns.map((c) => (
                <tr key={c.campaignId || c._id}>
                  <td>{c.description}</td>
                  <td>{c.ngoId}</td>
                  <td>
                    {c.expirationTime
                      ? new Date(c.expirationTime).toLocaleString()
                      : "No expiry"}
                  </td>
                  <td>{c.volunteerTime || "N/A"}</td>
                  <td>
                    <button onClick={() => openDonateModal(c)}>
                      Donate
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Donate Modal */}
      {donateModal.open && (
        <div
          style={{
            position: "fixed",
            top: 0, left: 0, right: 0, bottom: 0,
            background: "rgba(0,0,0,0.3)",
            display: "flex", alignItems: "center", justifyContent: "center",
            zIndex: 1000
          }}
        >
          <div style={{ background: "#fff", padding: 24, borderRadius: 8, minWidth: 300 }}>
            <h4>Donate to Campaign</h4>
            <p>
              <b>Description:</b> {donateModal.campaign.description}<br />
              <b>NGO ID:</b> {donateModal.campaign.ngoId}
            </p>
            <form onSubmit={handleDonate}>
              <input
                type="number"
                min="1"
                required
                placeholder="Amount"
                value={donateAmount}
                onChange={e => setDonateAmount(e.target.value)}
                style={{ width: "100%", marginBottom: 12 }}
              />
              <div>
                <button type="submit" disabled={donateStatus === "processing"}>
                  {donateStatus === "processing" ? "Processing..." : "Donate"}
                </button>
                <button type="button" onClick={closeDonateModal} style={{ marginLeft: 8 }}>
                  Cancel
                </button>
              </div>
            </form>
            {donateStatus === "success" && (
              <div style={{ color: "green", marginTop: 10 }}>Donation successful!</div>
            )}
            {donateStatus === "error" && (
              <div style={{ color: "red", marginTop: 10 }}>Donation failed. Please try again.</div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default DonorDashboard;
